// Get the first item (same pattern you used)
const inputDate = new Date($input.first().json.currentDate);   // "2025-05-26T10:22:34.868+01:00"

// Helper to zero-pad to two digits
const pad = n => n.toString().padStart(2, '0');

// Date  →  MM/DD/YYYY
const formattedDate = `${pad(inputDate.getMonth() + 1)}/${pad(inputDate.getDate())}/${inputDate.getFullYear()}`;

// Time  →  HH:mm:ss   (24-hour clock)
const formattedTime = `${pad(inputDate.getHours())}:${pad(inputDate.getMinutes())}:${pad(inputDate.getSeconds())}`;

return [
  {
    json: {
      formattedDate,   // e.g. "05/26/2025"
      formattedTime    // e.g. "09:22:34"
    }
  }
];

/**
 * n8n Function node — ultra-robust ElevenLabs transcript flattener
 * Paste into a Code node that comes right after the webhook.
 *
 * It tries VERY hard to find any human-readable text in messy payloads,
 * guesses the speaker role from nearby fields, merges consecutive lines,
 * and emits markdown + a few summary bullets.
 */

function secToClock(s) {
  if (s == null || isNaN(s)) return "";
  const total = Math.max(0, Math.floor(Number(s)));
  const m = String(Math.floor(total / 60)).padStart(2, "0");
  const ss = String(total % 60).padStart(2, "0");
  return `${m}:${ss}`;
}

function normRole(r) {
  const x = String(r || "").toLowerCase();
  if (/(agent|assistant|bot|lottie)/.test(x)) return "Lottie";
  if (/(user|caller|customer|client)/.test(x)) return "Caller";
  if (/system|server/.test(x)) return "System";
  return x ? x.charAt(0).toUpperCase() + x.slice(1) : "";
}

function isLikelyText(str) {
  if (typeof str !== "string") return false;
  const s = str.trim();
  if (!s) return false;
  // ignore obvious JSON / base64 blobs / single tokens
  if (/^[\[\{].*[\]\}]$/.test(s)) return false;
  if (/^[A-Za-z0-9+/=]{60,}$/.test(s)) return false;
  if (s.length < 2) return false;
  // prefer sentences or phrases with spaces/punctuation
  if (!/[ \t]|[.?!,:;]/.test(s)) return false;
  return true;
}

function pushTurn(turns, role, text, t) {
  if (!isLikelyText(text)) return;
  const r = role || "Agent";
  const last = turns[turns.length - 1];
  if (last && last.role === r) {
    last.text = (last.text + " " + text.trim()).trim();
    if (last.t == null && t != null) last.t = t;
  } else {
    turns.push({ t: t ?? null, role: r, text: text.trim() });
  }
}

function firstNumber(...vals) {
  for (const v of vals) {
    const n = Number(v);
    if (!isNaN(n) && isFinite(n)) return n;
  }
  return null;
}

// Deep scan for text with role/timestamp hints
function traverse(node, turns, ctx = {}) {
  if (node == null) return;

  if (typeof node === "string") {
    // try parse JSON; otherwise treat as free text
    try {
      const parsed = JSON.parse(node);
      traverse(parsed, turns, ctx);
    } catch {
      pushTurn(turns, ctx.role, node, ctx.t);
    }
    return;
  }

  if (Array.isArray(node)) {
    for (const item of node) traverse(item, turns, ctx);
    return;
  }

  // object
  const roleHint = normRole(node.role || node.speaker || node.agent_role || node.from || ctx.role);
  const tHint = firstNumber(
    node.elapsed_time, node.timestamp, node.time_s, node.start_time, node.offset, ctx.t
  );

  // Common direct text fields
  const directKeys = [
    "text","transcript","original_message","final_text","display_text","normalized_text",
    "utterance","delta","message","response","caption","content_text"
  ];
  for (const k of directKeys) {
    const v = node[k];
    if (typeof v === "string" && isLikelyText(v)) {
      pushTurn(turns, roleHint, v, tHint);
    } else if (v && typeof v === "object") {
      // message/content objects with .text
      const inner =
        v.text || v.transcript || v.display_text || v.normalized_text ||
        (v.content && (v.content.text || v.content.transcript || v.content.display_text));
      if (isLikelyText(inner)) pushTurn(turns, roleHint, inner, tHint);
    }
  }

  // Array containers that often hold text objects
  const arrayContainers = [
    "events","messages","outputs","output","choices","parts","segments","alternatives",
    "results","chunks","content","items","response_parts","agent_response","user_response"
  ];
  for (const key of arrayContainers) {
    const arr = node[key];
    if (!arr) continue;
    if (Array.isArray(arr)) {
      for (const it of arr) {
        // common shapes like {type:'output_text'|'input_text', text:'...'}
        if (it && typeof it === "object") {
          const roleFromType = it.type && /input/i.test(it.type) ? "Caller"
                               : it.type && /output|assistant|agent/i.test(it.type) ? "Lottie"
                               : null;
          const localRole = normRole(it.role || it.speaker || roleFromType || roleHint);
          const localT = firstNumber(it.elapsed_time, it.timestamp, it.time_s, tHint);

          // candidate text fields inside item
          const candidates = [
            it.text, it.transcript, it.final_text, it.display_text, it.normalized_text,
            it.delta, it.utterance
          ];
          for (const c of candidates) {
            if (typeof c === "string" && isLikelyText(c)) pushTurn(turns, localRole, c, localT);
          }
          // nested message/content
          if (it.message && typeof it.message === "object") {
            const m = it.message.text || it.message.transcript || it.message.content?.text;
            if (isLikelyText(m)) pushTurn(turns, localRole, m, localT);
          }
          if (it.content && typeof it.content === "object") {
            const c = it.content.text || it.content.transcript || it.content.display_text;
            if (isLikelyText(c)) pushTurn(turns, localRole, c, localT);
          }
          // recurse deeper
          traverse(it, turns, { role: localRole || roleHint, t: localT });
        } else if (typeof it === "string") {
          pushTurn(turns, roleHint, it, tHint);
        }
      }
    } else if (typeof arr === "object") {
      traverse(arr, turns, { role: roleHint, t: tHint });
    }
  }

  // Fallthrough: explore remaining nested objects
  for (const [k, v] of Object.entries(node)) {
    if (v && typeof v === "object" && !Array.isArray(v)) {
      if (arrayContainers.includes(k)) continue;
      traverse(v, turns, { role: roleHint, t: tHint });
    }
  }
}

// ----------------- MAIN -----------------
let raw = items[0].json;
if (typeof raw === "string") {
  try { raw = JSON.parse(raw); } catch { /* keep as string */ }
}

const turns = [];
traverse(raw, turns, { role: "", t: null });

// Final fallback: try to scrape ANY string fields in the whole object if still empty
if (turns.length === 0 && typeof raw === "object" && raw) {
  (function bruteStrings(obj, roleGuess) {
    if (obj == null) return;
    if (typeof obj === "string") {
      if (isLikelyText(obj)) pushTurn(turns, roleGuess || "Agent", obj, null);
      return;
    }
    if (Array.isArray(obj)) {
      for (const x of obj) bruteStrings(x, roleGuess);
      return;
    }
    const r = normRole(obj.role || obj.speaker || roleGuess);
    for (const [k, v] of Object.entries(obj)) {
      if (typeof v === "string" && isLikelyText(v)) {
        // guess role from key names
        const guess =
          /user|caller/i.test(k) ? "Caller" :
          /agent|assistant|bot/i.test(k) ? "Lottie" :
          r || roleGuess || "Agent";
        pushTurn(turns, guess, v, null);
      } else if (v && typeof v === "object") {
        bruteStrings(v, r || roleGuess);
      }
    }
  })(raw, "");
}

// If STILL nothing, emit a short snippet so the flow keeps going
if (turns.length === 0) {
  const snippet =
    typeof raw === "string" ? raw : JSON.stringify(raw || {}, null, 2);
  pushTurn(
    turns,
    "System",
    `No readable transcript found. Payload snippet:\n${(snippet || "").slice(0, 1200)}`,
    null
  );
}

// Deduplicate exact consecutive repeats
for (let i = turns.length - 1; i > 0; i--) {
  if (turns[i].role === turns[i - 1].role && turns[i].text === turns[i - 1].text) {
    turns.splice(i, 1);
  }
}

// Build markdown
const transcriptLines = turns.map(tr => {
  const clock = secToClock(tr.t);
  const prefix = clock ? `**${clock} ${tr.role}:**` : `**${tr.role}:**`;
  return `${prefix} ${tr.text}`;
});
const transcriptMarkdown = transcriptLines.join("\n\n");

// Quick summary heuristics
function pickFirstByRole(role) {
  const o = turns.find(t => t.role === role);
  return o ? o.text : "";
}
function pickLastByRole(role) {
  for (let i = turns.length - 1; i >= 0; i--) {
    if (turns[i].role === role) return turns[i].text;
  }
  return "";
}

const askCues = /(need|looking|can you|could you|help|would like|interested|question|arrang(e|ements)|travel|book|schedule|venue|hotel|train|flight)/i;
const actionCues = /(i will|we will|i can|we can|i’ll|we’ll|sending|schedule|book|follow up|follow-up|email you|set up|arrange)/i;

const asks = [];
const actions = [];
for (const t of turns) {
  const sentences = String(t.text || "").split(/(?<=[.!?])\s+/);
  for (const s of sentences) {
    const ss = s.trim();
    if (!ss) continue;
    if (t.role.toLowerCase() === "caller" && askCues.test(ss)) asks.push(ss);
    if (t.role.toLowerCase() === "lottie" && actionCues.test(ss)) actions.push(ss);
  }
}

const firstUser = pickFirstByRole("Caller");
const lastAgent = pickLastByRole("Lottie");

const summaryBullets = [
  firstUser ? `Caller’s opening: ${firstUser}` : null,
  asks.length ? `Key asks: ${asks.slice(0, 3).join(" | ")}` : null,
  actions.length ? `Agent commitments: ${actions.slice(0, 3).join(" | ")}` : null,
  lastAgent ? `Agent’s closing: ${lastAgent}` : null,
].filter(Boolean);

// Emit one clean item
return [
  {
    json: {
      transcript_lines: turns,            // [{ t, role, text }]
      transcript_markdown: transcriptMarkdown || "No readable transcript found.",
      summary_bullets: summaryBullets,    // [string]
      first_user_utterance: firstUser || "",
      last_agent_utterance: lastAgent || ""
    },
  },
];
