// n8n Code node: formats ElevenLabs transcript array into Markdown
// Input: $json.body.data.transcript (array of conversation turns)

const transcript = $json?.body?.data?.transcript || [];
const ttfb = $json?.body?.data?.convai_tts_service_ttfb?.elapsed_time;
const llmUsage = $json?.body?.data?.llm_usage || null;
const ragInfo = $json?.body?.data?.rag_retrieval_info || null;

// -------- Helpers --------
const toFixedOr = (n, d = 3) => (typeof n === "number" && isFinite(n) ? n.toFixed(d) : "n/a");
const roleIcon = (role) => {
  const r = (role || "").toLowerCase();
  if (r === "user") return "ðŸ‘¤";
  if (r === "agent" || r === "assistant") return "ðŸ¤–";
  if (r === "system") return "ðŸ› ï¸";
  return "ðŸ’¬";
};
const extractMessageText = (turn) => {
  if (typeof turn.message === "string") return turn.message;
  if (typeof turn.content === "string") return turn.content;
  if (Array.isArray(turn.content)) {
    const firstText = turn.content.find((c) => typeof c?.text === "string")?.text;
    if (firstText) return firstText;
  }
  if (turn.tool_calls?.length) return `[tool calls: ${turn.tool_calls.length}]`;
  if (turn.tool_results?.length) return `[tool results: ${turn.tool_results.length}]`;
  return "";
};

// -------- Build Markdown Transcript --------
let md = `# ðŸ—£ï¸ Conversation Transcript\n\n`;
md += `**Total Turns:** ${transcript.length}\n`;
if (ttfb !== undefined) md += `**TTS TTFB:** ${toFixedOr(ttfb, 3)}s\n\n`;

// Add LLM usage if available
if (llmUsage?.model_usage) {
  const model = Object.keys(llmUsage.model_usage)[0];
  const usage = llmUsage.model_usage[model];
  md += `### ðŸ“Š LLM Usage (${model})\n`;
  if (usage.input) md += `- Input tokens: ${usage.input.tokens} ($${usage.input.price.toFixed(8)})\n`;
  if (usage.output_total)
    md += `- Output tokens: ${usage.output_total.tokens} ($${usage.output_total.price.toFixed(8)})\n`;
  md += `\n`;
}

// Add RAG retrieval info if present
if (ragInfo) {
  md += `### ðŸ“š RAG Retrieval Info\n`;
  md += "```json\n" + JSON.stringify(ragInfo, null, 2) + "\n```\n\n";
}

// Process turns
transcript.forEach((turn, idx) => {
  const role = turn.role || (turn.agent_metadata ? "agent" : "message");
  const icon = roleIcon(role);
  const text = extractMessageText(turn);
  const secs = turn.time_in_call_secs;
  const metrics = turn.conversation_turn_metrics?.metrics || null;

  md += `**${idx + 1}. ${icon} ${role.toUpperCase()}**`;
  if (typeof secs === "number") md += ` Â· t=${secs}s`;
  md += "\n";

  if (text) {
    md += text.trim() + "\n";
  } else {
    const stripped = {
      tool_calls: turn.tool_calls,
      tool_results: turn.tool_results,
      feedback: turn.feedback,
      interrupted: turn.interrupted,
      source_medium: turn.source_medium,
    };
    md += "```json\n" + JSON.stringify(stripped, null, 2) + "\n```\n";
  }

  // Optional per-turn metrics
  if (metrics?.convai_asr_trailing_service_latency?.elapsed_time) {
    md += `_ASR trailing latency: ${toFixedOr(metrics.convai_asr_trailing_service_latency.elapsed_time)}s_\n`;
  }

  md += "\n"; // spacing between turns
});

// -------- Output Markdown --------
return [{ markdown: md }];
