  if (obj == null || obj === "" || (Array.isArray(obj) && obj.length === 0)) return false;
  if (typeof obj === "object" && !Array.isArray(obj)) {
    return Object.values(obj).some(v => hasUsefulData(v));
  }
  return true;
}
if (!hasUsefulData(parsed)) parsed = { message: "No info found" };

// If array, take first
if (Array.isArray(parsed)) parsed = parsed[0] ?? { message: "No info found" };

// Deep flatten into dot paths and snake paths
function toSnake(s) {
  return s
    .replace(/[^a-zA-Z0-9]+/g, "_")
    .replace(/_+/g, "_")
    .replace(/^_|_$/g, "")
    .toLowerCase();
}

function flatten(obj, prefix = "", out = {}) {
  if (obj == null) {
    out[prefix] = obj;
    return out;
  }
  if (typeof obj !== "object" || obj instanceof Date) {
    if (prefix) out[prefix] = obj;
    return out;
  }
  if (Array.isArray(obj)) {
    // Represent arrays as JSON strings to keep primitives
    if (prefix) out[prefix] = JSON.stringify(obj);
    return out;
  }
  for (const [k, v] of Object.entries(obj)) {
    const dotKey = prefix ? `${prefix}.${k}` : k;
    flatten(v, dotKey, out);
  }
  return out;
}

// Coercion helpers
function toISODateString(x) {
  const d = new Date(x);
  return isNaN(d.getTime()) ? null : d.toISOString().slice(0, 10);
}

function coercePrimitive(val) {
  if (val == null) return "";
  if (typeof val === "string") return val.trim();
  if (typeof val === "number" || typeof val === "boolean") return val;
  if (val instanceof Date) return toISODateString(val) ?? val.toISOString();
  // objects and arrays become JSON
  return JSON.stringify(val);
}

// Normalise known fields on the original object first
if (parsed.lastUpdated != null) {
  const iso = toISODateString(parsed.lastUpdated);
  parsed.lastUpdated = iso ?? String(parsed.lastUpdated).trim();
}
if (parsed.website != null) {
  let w = String(parsed.website).trim();
  w = w.replace(/^https?:\/\//i, "").replace(/\/+$/g, "");
  parsed.website = w;
}

// Coerce all top-level props to primitives
for (const k of Object.keys(parsed)) {
  parsed[k] = coercePrimitive(parsed[k]);
}

// Build flattened maps
const flatDot = flatten(parsed);
const flatSnake = {};
for (const [k, v] of Object.entries(flatDot)) {
  flatSnake[toSnake(k)] = v;
}

// Coerce all flattened values to primitives
for (const k of Object.keys(flatDot)) {
  flatDot[k] = coercePrimitive(flatDot[k]);
}
for (const k of Object.keys(flatSnake)) {
  flatSnake[k] = coercePrimitive(flatSnake[k]);
}

// Provide stable aliases for common merge keys
const aliases = {
  fullName: parsed.fullName ?? flatDot["fullName"] ?? "",
  actualCompanyName: parsed.actualCompanyName ?? flatDot["actualCompanyName"] ?? "",
  website: parsed.website ?? flatDot["website"] ?? "",
  lastUpdated: parsed.lastUpdated ?? flatDot["lastUpdated"] ?? ""
};

// Build a composite merge key
const __mergeKey = [aliases.fullName, aliases.actualCompanyName, aliases.website]
  .map(x => String(x || "").toLowerCase().trim())
  .join("|");

// Final payload
const out = {
  ...parsed,
  ...flatDot,
  ...flatSnake,
  ...aliases,
  __mergeKey
};

// Ensure strings for all merge candidates
for (const k of ["fullName", "actualCompanyName", "website", "lastUpdated", "__mergeKey"]) {
  if (k in out) out[k] = coercePrimitive(out[k]);
}

// De-duplicate keys case-insensitively
// Keeps the first occurrence (from parsed), drops later duplicates (from flatSnake etc.)
const seenLower = new Set();
for (const key of Object.keys(out)) {
  const lower = key.toLowerCase();
  if (seenLower.has(lower)) {
    delete out[key];
  } else {
    seenLower.add(lower);
  }
}

// Return n8n-compatible single item
return [
  {
    json: out
  }
];

