// ONE Code node: read from 'data-producer', merge binaries, count data*, build Outlook attachments,
// and (optionally) merge JSON from existing 'Airtable' node â€” no new nodes added.

const SOURCE_NODE = 'data-producer';
const AIRTABLE_NODE = 'Airtable';

// --- 1) Load source items (from named node; fallback to current input) ---
let srcItems;
try {
  srcItems = $items(SOURCE_NODE);
  if (!Array.isArray(srcItems) || srcItems.length === 0) srcItems = items;
} catch (_) {
  srcItems = items;
}

// --- 2) Merge binaries into a single item; prefer json.fieldName or data{index} ---
const merged = { json: {}, binary: {} };

for (const item of srcItems) {
  const impliedKey = item.json?.fieldName || (item.json?.index ? `data${item.json.index}` : undefined);

  if (impliedKey && item.binary?.[impliedKey]) {
    merged.binary[impliedKey] = item.binary[impliedKey];
    if (item.json?.filename) merged.binary[impliedKey].fileName = item.json.filename;
    continue;
  }

  if (item.binary) {
    for (const [bKey, bVal] of Object.entries(item.binary)) {
      merged.binary[bKey] = bVal; // last one wins if duplicate keys
      if (item.json?.filename && impliedKey === bKey) {
        merged.binary[bKey].fileName = item.json.filename;
      }
    }
  }
}

// --- 3) Count data* binaries (total + unique) and per-item breakdown ---
const dataKeyRegex = /^data\d*$/i;

let totalDataKeys = 0;
const uniqueDataKeys = new Set();
const perItem = [];

for (let i = 0; i < srcItems.length; i++) {
  const bin = srcItems[i].binary || {};
  const keys = Object.keys(bin).filter(k => dataKeyRegex.test(k));
  perItem.push({ itemIndex: i + 1, dataKeys: keys, count: keys.length });
  totalDataKeys += keys.length;
  keys.forEach(k => uniqueDataKeys.add(k));
}

// --- 4) Build Outlook-friendly attachments array (JSON Parameters = ON) ---
const allKeys = Object.keys(merged.binary || {});
merged.json.attachments = allKeys.map(k => ({
  binaryPropertyName: k,
  fileName: merged.binary[k]?.fileName,
  mimeType: merged.binary[k]?.mimeType,
}));

// --- 5) (Optional) Merge JSON from existing 'Airtable' node (no new nodes created) ---
try {
  const airtableItems = $items(AIRTABLE_NODE); // will throw if node doesn't exist
  if (Array.isArray(airtableItems) && airtableItems.length > 0) {
    const airtableJson = airtableItems[0]?.json ?? {};

    // (a) Shallow-merge ALL top-level Airtable fields into merged.json (last write wins)
    for (const [k, v] of Object.entries(airtableJson)) {
      merged.json[k] = v;
    }

    // (b) Keep a namespaced copy to avoid key collisions / for debugging
    merged.json.airtable = airtableJson;

    // (c) If there's an Airtable "Attachments" array, add a flattened read-friendly view
    if (Array.isArray(airtableJson.Attachments)) {
      merged.json.airtableAttachments = airtableJson.Attachments.map(att => ({
        id: att?.id,
        url: att?.url,
        filename: att?.filename,
        size: att?.size,
        type: att?.type,
      }));
    }
  }
} catch (_) {
  // If 'Airtable' node isn't present, do nothing.
}

// --- 6) Summary meta (explicit totals) ---
merged.json.sourceNode = SOURCE_NODE;
merged.json.totalItems = srcItems.length;
merged.json.totalItemsFromSource = srcItems.length;
merged.json.totalBinaryKeys = allKeys.length;
merged.json.totalDataOutputs = totalDataKeys;
merged.json.uniqueDataOutputs = uniqueDataKeys.size;
merged.json.uniqueDataKeys = Array.from(uniqueDataKeys);
merged.json.perItem = perItem;

// --- 7) Return ONE merged item ---
return [merged];

// n8n Code node (JavaScript)

// Process all incoming items
const items = $input.all();

function toDate(val) {
  if (val == null) return null;

  if (typeof val === 'number') {
    const ms = val < 1e11 ? val * 1000 : val;
    const d = new Date(ms);
    return isNaN(d.getTime()) ? null : d;
  }

  if (val instanceof Date) return isNaN(val.getTime()) ? null : val;

  if (typeof val === 'string') {
    const s = val.trim();
    let d = new Date(s);
    if (!isNaN(d.getTime())) return d;

    d = new Date(s.replace(' ', 'T'));
    if (!isNaN(d.getTime())) return d;

    if (!/[zZ]|[+\-]\d{2}:\d{2}$/.test(s)) {
      d = new Date(s + 'Z');
      if (!isNaN(d.getTime())) return d;
    }
  }

  return null;
}

function ordinal(n) {
  const v = Math.abs(n);
  const m100 = v % 100;
  if (m100 >= 11 && m100 <= 13) return 'th';
  switch (v % 10) {
    case 1: return 'st';
    case 2: return 'nd';
    case 3: return 'rd';
    default: return 'th';
  }
}

function londonParts(date) {
  const zone = 'Europe/London';

  const base = new Intl.DateTimeFormat('en-GB', {
    timeZone: zone,
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    weekday: 'long',
  }).formatToParts(date);

  const get = (t) => base.find(p => p.type === t)?.value;

  const dayNum = Number(get('day'));
  const monthName = get('month');
  const year = get('year');
  const weekday = get('weekday');

  const time12London = new Intl.DateTimeFormat('en-GB', {
    timeZone: zone,
    hour: '2-digit',
    minute: '2-digit',
    hour12: true,
  }).format(date);

  const time24London = new Intl.DateTimeFormat('en-GB', {
    timeZone: zone,
    hour: '2-digit',
    minute: '2-digit',
    hour12: false,
  }).format(date);

  return { dayNum, monthName, year, weekday, time12London, time24London };
}

function pickConversationDateSource(j) {
  // Prefer explicit Conversation Date in fields, then top-level
  let v =
    j?.fields?.['Conversation Date'] ??
    j?.['Conversation Date'] ??
    j?.createdTime ?? // Airtable record createdTime as a safe fallback
    j?.input ??
    j?.timestamp ??
    j?.date ??
    null;

  // If Airtable ever returns an array or object, coerce to string if possible
  if (Array.isArray(v)) v = v[0] ?? null;
  if (v && typeof v === 'object' && 'value' in v) v = v.value;

  return v;
}

return items.map(item => {
  const raw = pickConversationDateSource(item.json);
  const d = toDate(raw);

  if (!d) {
    return {
      json: {
        ...item.json,
        conversationDateRaw: raw,
        error: 'Invalid or missing Conversation Date. Provide ISO string or epoch seconds/milliseconds in fields["Conversation Date"] or top-level "Conversation Date".',
      },
    };
  }

  const { dayNum, monthName, year, weekday, time12London, time24London } = londonParts(d);

  const dayWithOrdinal = `${dayNum}${ordinal(dayNum)}`;
  const dateUK = `${dayWithOrdinal} ${monthName} ${year}`;

  return {
    json: {
      ...item.json,
      conversationDateRaw: raw,
      dayOfWeek: weekday,
      dateUK,
      dayWithOrdinal,
      month: monthName,
      time12London,
      time24London,
      timeZone: 'Europe/London',
    },
  };
});

