// --- Function node code ---
// Get the first item (same pattern you used)
const inputDate = new Date($input.first().json.currentDate);   // "2025-05-26T10:22:34.868+01:00"

// Helper to zero-pad to two digits
const pad = n => n.toString().padStart(2, '0');

// Date  ‚Üí  MM/DD/YYYY
const formattedDate = `${pad(inputDate.getMonth() + 1)}/${pad(inputDate.getDate())}/${inputDate.getFullYear()}`;

// Time  ‚Üí  HH:mm:ss   (24-hour clock)
const formattedTime = `${pad(inputDate.getHours())}:${pad(inputDate.getMinutes())}:${pad(inputDate.getSeconds())}`;

return [
  {
    json: {
      formattedDate,   // e.g. "05/26/2025"
      formattedTime    // e.g. "09:22:34"
    }
  }
];

/**
 * Input examples this handles:
 * 1) A stringified JSON like "{\"data\":[{\"data\":[{\"bio\":\"Ray Meiring ...\"}]}]}"
 * 2) An object like { data: [ { data: [ { bio: "..." }, ... ] } ] }
 * 3) A Perplexity-style { biz: "{\"data\":[{\"output\":\"...\"}]}" } or { biz: { data:[{output:"..."}] } }
 * 4) A long string that contains both escaped JSON and a readable tail (fallback: strip the JSON-looking prefix)
 *
 * Output:
 * { text: "<single clean text stream>" }
 */

function safeParse(x) {
  if (x == null) return null;
  if (typeof x === 'object') return x;
  if (typeof x === 'string') {
    try { return JSON.parse(x); } catch { return null; }
  }
  return null;
}

// Collect all "bio" strings recursively
function collectBios(obj, out) {
  if (!obj) return;
  if (typeof obj === 'string') return;
  if (Array.isArray(obj)) { obj.forEach(v => collectBios(v, out)); return; }
  if (typeof obj === 'object') {
    if (typeof obj.bio === 'string' && obj.bio.trim()) out.push(obj.bio.trim());
    for (const k of Object.keys(obj)) collectBios(obj[k], out);
  }
}

// Try to extract Perplexity-like "output" fields
function collectOutputs(obj, out) {
  if (!obj) return;
  if (typeof obj === 'string') return;
  if (Array.isArray(obj)) { obj.forEach(v => collectOutputs(v, out)); return; }
  if (typeof obj === 'object') {
    if (typeof obj.output === 'string' && obj.output.trim()) out.push(obj.output.trim());
    for (const k of Object.keys(obj)) collectOutputs(obj[k], out);
  }
}

// Fallback: remove a leading escaped-JSON prefix and keep the human-readable tail
function stripEscapedJsonPrefix(s) {
  if (typeof s !== 'string') return '';
  // Heuristic: if it starts with "{\"" or '{"data":', cut everything up to the first " - " and keep the rest
  if (/^\\?{\\?"/.test(s) || s.startsWith('{"data"')) {
    const idx = s.indexOf(' - ');
    if (idx !== -1) return s.slice(idx + 3).trim();
  }
  // Also unescape common \" artifacts
  return s.replace(/\\"/g, '"').replace(/\\n/g, '\n').replace(/\\t/g, '\t').trim();
}

// ---- main ----
const input = $input.item.json;

// 1) Find candidate sources
// - direct text field
let textRaw = input.text || input.output || input.bio || null;

// - people/text payload often under textPayload (from your merge packing), or directly at root
const peopleObj = input.textPayload || input.peoplePayload || input;

// - biz payload (could be string or object)
let bizObj = input.bizPayload || input.biz || null;
bizObj = safeParse(bizObj) || bizObj; // parse if string

// 2) Try to assemble text from structured objects first
const bios = [];
collectBios(peopleObj, bios);

const bizOutputs = [];
collectOutputs(bizObj, bizOutputs);

// 3) If we didn‚Äôt get anything, try to parse the root if it‚Äôs a stringified JSON
if (!bios.length && typeof input === 'string') {
  const parsed = safeParse(input);
  if (parsed) collectBios(parsed, bios);
}

// 4) Build the final text
let blocks = [];

// prefer bios (richest text you showed)
if (bios.length) blocks.push(bios.join('\n\n'));

// include biz output if present
if (bizOutputs.length) blocks.push(bizOutputs.join('\n\n'));

// if still nothing, fall back to stripping messy string content
if (!blocks.length) {
  // Pick the most likely raw string field to clean
  const candidates = [
    textRaw,
    typeof input === 'string' ? input : null,
    typeof input?.peoplePayload === 'string' ? input.peoplePayload : null,
    typeof input?.biz === 'string' ? input.biz : null
  ].filter(Boolean);

  if (candidates.length) {
    const cleaned = stripEscapedJsonPrefix(candidates[0]);
    if (cleaned) blocks.push(cleaned);
  }
}

// As a last resort, emit something explicit
if (!blocks.length) blocks.push('No readable text content found.');

// Output single text stream
return [{
  json: {
    text: blocks.join('\n\n---\n\n')
  }
}];

const s = $getWorkflowStaticData('node');

s.bag ||= {};

const { type, correlationId, data } = $json;
if (!correlationId) return [];

s.bag[correlationId] ||= { A: null, B: null };
if (type === 'A') s.bag[correlationId].A = data;
if (type === 'B') s.bag[correlationId].B = data;

const entry = s.bag[correlationId];
if (entry.A && entry.B) {
