  delete s.bag[correlationId];
  return [{ json: out }];
}
return []; // keep waiting

const s = $getWorkflowStaticData('node');
s.bag = s.bag || {};
const { type, correlationId, data } = $json;
if (!correlationId) { return []; }
if (!s.bag[correlationId]) { s.bag[correlationId] = { A: null, B: null, timeout: false }; }
if (type === 'A') s.bag[correlationId].A = data;
if (type === 'B') s.bag[correlationId].B = data;
if (type === 'timeout') s.bag[correlationId].timeout = true;
const entry = s.bag[correlationId];
if (entry.A && entry.B) {
  const out = { correlationId, fromA: entry.A, fromB: entry.B };
  delete s.bag[correlationId];
  return [{ json: out }];
}
if (entry.timeout && !(entry.A && entry.B)) {
  const out = { status: 'timeout', correlationId, haveA: !!entry.A, haveB: !!entry.B };
  delete s.bag[correlationId];
  return [{ json: out }];
}
return [];
