// Find first free slot in [windowStart, windowEnd) using scheduleItems from the getSchedule node,
// rounding start up to the next 15-minute boundary (00/15/30/45).

// CHANGE this to your getSchedule node name
const GET_SCHEDULE_NODE = "Outlook find time in next 60m";

const res = $node[GET_SCHEDULE_NODE].json.value?.[0];
const items = (res && res.scheduleItems) ? res.scheduleItems : [];

const zone = 'Europe/London';
const now = DateTime.now().setZone(zone);

// Read inputs from prior node (this item). Fallbacks if missing.
let windowStart = $json.windowStart
  ? DateTime.fromISO($json.windowStart, { zone })
  : now;
let windowEnd = $json.windowEnd
  ? DateTime.fromISO($json.windowEnd, { zone })
  : windowStart.plus({ minutes: 60 });

const durationMin = Number.isFinite($json.durationMinutes) ? $json.durationMinutes : 15;

// Helpers
function roundUpToQuarter(dt) {
  let t = dt.set({ second: 0, millisecond: 0 });
  const m = t.minute % 15;
  return m === 0 ? t : t.plus({ minutes: 15 - m });
}
function fmtLocalISOWithOffset(dt) {
  // Local time with +00:00 / +01:00 and correct DST label for that datetime
  return dt.toISO({ suppressMilliseconds: true, includeOffset: true });
}
function fmtUTC(dt) {
  // UTC timestamp (Z) for systems that prefer absolute time
  return dt.setZone('UTC').toISO({ suppressMilliseconds: true, includeOffset: true });
}

// Ensure windowStart isn't in the past and is snapped to next quarter
if (windowStart < now) windowStart = now;
windowStart = roundUpToQuarter(windowStart);

// Validate window
if (windowEnd <= windowStart) {
  return [{ error: "Invalid window: end is before or equal to start." }];
}

// Normalize & sort busy items that overlap the window
const busy = items
  .map(i => ({
    start: DateTime.fromISO(i.start.dateTime, { zone }),
    end:   DateTime.fromISO(i.end.dateTime,   { zone }),
  }))
  .filter(i => i.end > windowStart && i.start < windowEnd)
  .sort((a, b) => a.start - b.start);

// Scan for the first free gap >= durationMin
let cursor = windowStart;

for (const b of busy) {
  if (b.start > cursor) {
    const gapEnd = b.start < windowEnd ? b.start : windowEnd;
    if (gapEnd.diff(cursor, 'minutes').minutes >= durationMin) {
      const slotStart = cursor;
      const slotEnd = slotStart.plus({ minutes: durationMin });
      return [{
        slotStart: fmtLocalISOWithOffset(slotStart),
        slotEnd:   fmtLocalISOWithOffset(slotEnd),
        timeZone:  slotStart.offsetNameShort,       // "BST" or "GMT" for the slot time
        slotStartUTC: fmtUTC(slotStart),
        slotEndUTC:   fmtUTC(slotEnd),
        // Optional if you need Windows zone for Graph later:
        // timeZoneWindows: "GMT Standard Time"
      }];
    }
  }
  // Move cursor to just after this busy block, snapped to next quarter
  if (b.end > cursor) cursor = roundUpToQuarter(b.end);

  if (cursor >= windowEnd) {
    return [{ error: `No free ${durationMin}-minute slot in the requested window.` }];
  }
}

// Tail after last busy block
if (windowEnd.diff(cursor, 'minutes').minutes >= durationMin) {
  const slotStart = cursor;
  const slotEnd = slotStart.plus({ minutes: durationMin });
  return [{
    slotStart: fmtLocalISOWithOffset(slotStart),
    slotEnd:   fmtLocalISOWithOffset(slotEnd),
    timeZone:  slotStart.offsetNameShort,         // "BST" or "GMT" for the slot time
    slotStartUTC: fmtUTC(slotStart),
    slotEndUTC:   fmtUTC(slotEnd),
    // timeZoneWindows: "GMT Standard Time"
  }];
}

// Nothing found
return [{ error: `No free ${durationMin}-minute slot in the requested window.` }];

