// --- Function node code ---

// Expecting an input item with a field `now`
// e.g. "2025-12-02T06:57:39.432+00:00"
const input = $input.first();
const inputDate = new Date(input.json.now);

// Helper to zero-pad to two digits
const pad = n => n.toString().padStart(2, '0');

// UK date ‚Üí DDMMYY
const dd = pad(inputDate.getDate());
const mm = pad(inputDate.getMonth() + 1);
const yy = inputDate.getFullYear().toString().slice(-2);

const formattedDate = `${dd}${mm}${yy}`;

// Time ‚Üí HH:mm:ss (24-hour clock)
const formattedTime = `${pad(inputDate.getHours())}:${pad(inputDate.getMinutes())}:${pad(inputDate.getSeconds())}`;

// Local UK-style date ‚Üí DD/MM/YYYY
const localDate = inputDate.toLocaleDateString("en-GB");

// Local UK-style time ‚Üí HH:mm:ss (24-hour clock)
const localTime = inputDate.toLocaleTimeString("en-GB", {
  hour12: false
});

return [
  {
    json: {
      now: input.json.now,   // original value from Set node
      formattedDate,         // "021225"
      formattedTime,         // "06:57:39"
      localDate,             // "02/12/2025"
      localTime              // "06:57:39"
    }
  }
];
