// Check if we have data
if (!items || items.length === 0) {
  throw new Error('No input data received');
}

const response = items[0].json;

// Validate the response structure
if (!response?.data?.sentences || !response?.data?.speakers) {
  throw new Error('Invalid data structure - missing sentences or speakers');
}

const meetingData = response.data;

// Create clean transcript with speaker changes only
let transcript = '';
let lastSpeaker = null;

meetingData.sentences.forEach(sentence => {
  const speakerName = sentence.speaker_name;
  const text = sentence.ai_filters?.text_cleanup || sentence.text || '';

  // Only add speaker name if it's different from the last one
  if (speakerName !== lastSpeaker) {
    transcript += `\n${speakerName}:\n`;
    lastSpeaker = speakerName;
  }

  transcript += `${text} `;
});

// Extract unique speakers
const speakers = meetingData.speakers.map(speaker => speaker.name);

// Return the processed data with id
return [{
  json: {
    id: meetingData.id,
    transcript: transcript.trim(),
    speakers: speakers
  }
}];

