// Get the first item (same pattern you used)
const inputDate = new Date($input.first().json.currentDate);   // "2025-05-26T10:22:34.868+01:00"

// Helper to zero-pad to two digits
const pad = n => n.toString().padStart(2, '0');

// Date  →  MM/DD/YYYY
const formattedDate = `${pad(inputDate.getMonth() + 1)}/${pad(inputDate.getDate())}/${inputDate.getFullYear()}`;

// Time  →  HH:mm:ss   (24-hour clock)
const formattedTime = `${pad(inputDate.getHours())}:${pad(inputDate.getMinutes())}:${pad(inputDate.getSeconds())}`;

return [
  {
    json: {
      formattedDate,   // e.g. "05/26/2025"
      formattedTime    // e.g. "09:22:34"
    }
  }
];

// This node normalises the AI Agent output into:
// { partner_short_summary: string, new_opportunity_description: string }
//
// It is defensive:
// - If the AI returned double-wrapped JSON, we parse it.
// - If parsing fails (e.g. "Please provide..." text), we just use the raw text as the summary.

const out = [];

for (const item of $input.all()) {
  let value = item.json["0"]; // What the AI Agent returned in field "0"
  let partner_short_summary = '';
  let new_opportunity_description = '';

  // Helper to try to extract from a parsed JSON object
  const extractFromInner = (inner) => {
    return {
      partner_short_summary:
        inner.partner_short_summary ||
        inner.partner_high_level_summary ||
        '',
      new_opportunity_description:
        inner.new_opportunity_description ||
        inner.new_opportunity ||
        '',
    };
  };

  try {
    if (typeof value === 'string') {
      // First attempt: value is a JSON string like "{\"text\":\"{...}\"}"
      let outer;
      try {
        outer = JSON.parse(value);
      } catch {
        outer = null;
      }

      if (outer && typeof outer.text === 'string') {
        // outer.text might itself be JSON or just plain text
        try {
          const inner = JSON.parse(outer.text);
          const extracted = extractFromInner(inner);
          partner_short_summary = extracted.partner_short_summary;
          new_opportunity_description = extracted.new_opportunity_description;
        } catch {
          // outer.text was NOT JSON – treat as plain summary
          partner_short_summary = outer.text;
          new_opportunity_description = '';
        }
      } else {
        // value might be the inner JSON directly
        try {
          const inner = JSON.parse(value);
          const extracted = extractFromInner(inner);
          partner_short_summary = extracted.partner_short_summary;
          new_opportunity_description = extracted.new_opportunity_description;
        } catch {
          // Not JSON at all – treat full string as summary
          partner_short_summary = value;
          new_opportunity_description = '';
        }
      }
    } else if (value && typeof value === 'object') {
      // In case the AI node already gave us an object instead of a string
      if (typeof value.text === 'string') {
        try {
          const inner = JSON.parse(value.text);
          const extracted = extractFromInner(inner);
          partner_short_summary = extracted.partner_short_summary;
          new_opportunity_description = extracted.new_opportunity_description;
        } catch {
          partner_short_summary = value.text;
          new_opportunity_description = '';
        }
      } else {
        const extracted = extractFromInner(value);
        partner_short_summary = extracted.partner_short_summary;
        new_opportunity_description = extracted.new_opportunity_description;
      }
    } else {
      // Fallback – unexpected format
      partner_short_summary = '';
      new_opportunity_description = '';
    }
  } catch (e) {
    // Absolute fallback – never kill the workflow, just emit empty fields
    partner_short_summary = '';
    new_opportunity_description = '';
  }

  out.push({
    json: {
      partner_short_summary,
      new_opportunity_description,
    },
  });
}

return out;
