/**
 * n8n Code node (JavaScript)
 * Purpose: Take an agent "output" string and return a structured JSON payload
 *          suitable for a later Outlook draft email step (PLAIN TEXT).
 *
 * Output (per item):
 *  - json.draft: Graph-like message object (subject, body Text, toRecipients)
 *  - json.fields: flat convenience fields (toEmail, toName, subject, bodyText, bodyHtml)
 *  - json.extracted: other extracted metadata
 *  - json.raw: original output
 *
 * Works with BOTH formats:
 *  - Markdown style: **Recipient Name:**, **Recipient Email:**, **Subject:**, **Main Email Copy:**
 *  - Plain style: Recipient Name:, Recipient's Name:, Recipient Email:, Subject:, Subject Line:, Main Email Copy:
 */

function asString(v) {
  if (v === null || v === undefined) return '';
  if (typeof v === 'string') return v;
  try { return JSON.stringify(v); } catch { return String(v); }
}

function cleanLine(s) {
  return asString(s)
    .replace(/\r\n/g, '\n')
    .replace(/\r/g, '\n')
    .trim();
}

function stripBackticks(s) {
  return asString(s).replace(/`+/g, '').trim();
}

function firstMatch(text, regex) {
  const m = text.match(regex);
  return m && m[1] ? m[1].trim() : '';
}

function firstMatchAny(text, regexList) {
  for (const re of regexList) {
    const v = firstMatch(text, re);
    if (v) return v;
  }
  return '';
}

function normaliseEmail(raw) {
  let s = stripBackticks(raw || '');
  s = s.replace(/\s*\(.*?\)\s*$/g, '').trim();
  const m = s.match(/[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}/i);
  return m ? m[0] : s;
}

function stripCodeFences(s) {
  return s
    .replace(/```[\s\S]*?```/g, (m) => m.replace(/^```[^\n]*\n?/, '').replace(/```$/, ''))
    .replace(/~~~[\s\S]*?~~~/g, (m) => m.replace(/^~~~[^\n]*\n?/, '').replace(/~~~$/, ''));
}

function markdownToPlainText(s) {
  let t = asString(s);

  t = t.replace(/\r\n/g, '\n').replace(/\r/g, '\n');
  t = stripCodeFences(t);

  t = t.replace(/^\s{0,3}#{1,6}\s+/gm, '');
  t = t.replace(/\*\*(.*?)\*\*/g, '$1');
  t = t.replace(/\*(.*?)\*/g, '$1');
  t = t.replace(/_(.*?)_/g, '$1');
  t = t.replace(/\[(.*?)\]\((.*?)\)/g, '$1');
  t = t.replace(/`([^`]+)`/g, '$1');

  t = t.replace(/^\s*[•·]\s+/gm, '- ');
  t = t.replace(/^\s*[\-\*]\s+/gm, '- ');

  t = t.replace(/[“”]/g, '"').replace(/[‘’]/g, "'");
  t = t.replace(/\u00A0/g, ' ');

  t = t.replace(/\n{3,}/g, '\n\n');

  return t.trim();
}

// Optional case detail extraction (kept from your node). Safe if not present.
function extractCaseDetails(text) {
  const t = text;

  const caseNumber =
    (t.match(/CASE\s*NUMBER:\s*([A-Za-z0-9\/\-]+)/i) || [])[1] || '';

  const tribunal =
    (t.match(/IN\s+THE\s+([A-Z][A-Z\s]+EMPLOYMENT\s+TRIBUNAL)/i) || [])[1] || '';

  const claimant =
    (t.match(/BETWEEN:\s*([\s\S]*?)\(\s*Claimant\s*\)/i) || [])[1] || '';
  const respondent =
    (t.match(/-+\s*V\s*-+\s*([\s\S]*?)\(\s*Respondent\s*\)/i) || [])[1] || '';

  const claimantClean = claimant.replace(/\s+/g, ' ').trim();
  const respondentClean = respondent.replace(/\s+/g, ' ').trim();

  const dismissalDate =
    (t.match(/dismissed\s+for\s+capability[\s\S]*?\s+on\s+(\d{1,2}\s+[A-Za-z]+\s+\d{4})/i) || [])[1] || '';

  return { caseNumber, tribunal, claimant: claimantClean, respondent: respondentClean, dismissalDate };
}

const results = [];

for (const item of items) {
  // Pull forward any existing fields from upstream
  const fields = { ...(item.json.fields || {}) };

  const rawOutput = cleanLine(
    item.json.output ?? item.json.raw?.output ?? item.json.text ?? item.json.body ?? item.json
  );

  // Accept straight and curly apostrophes in "Recipient's"
  const apos = "['’]";

  // Extract recipient + subject, robust to plain or markdown label formats
  const recipientName = firstMatchAny(rawOutput, [
    /\*\*Recipient Name:\*\*\s*([^\n]+)/i,
    new RegExp(`^\\s*Recipient${apos}?s?\\s*Name:\\s*(.+)$`, 'im'),
    /^\s*Recipient Name:\s*(.+)$/im
  ]);

  const recipientTitle = firstMatchAny(rawOutput, [
    /\*\*Recipient Title:\*\*\s*([^\n]+)/i,
    new RegExp(`^\\s*Recipient${apos}?s?\\s*Title:\\s*(.+)$`, 'im'),
    /^\s*Recipient Title:\s*(.+)$/im
  ]);

  const recipientEmailLine = firstMatchAny(rawOutput, [
    /\*\*Recipient Email:\*\*\s*([^\n]+)/i,
    /\*\*Suggested Email Address:\*\*\s*([^\n]+)/i,
    new RegExp(`^\\s*Recipient${apos}?s?\\s*Email:\\s*(.+)$`, 'im'),
    /^\s*Recipient Email:\s*(.+)$/im,
    /^\s*Suggested Email Address:\s*(.+)$/im
  ]);

  const toEmailExtracted = normaliseEmail(recipientEmailLine);

  const subjectExtracted = firstMatchAny(rawOutput, [
    /\*\*Subject:\*\*\s*([^\n]+)/i,
    /^\s*Subject:\s*(.+)$/im,
    /^\s*Subject Line:\s*(.+)$/im
  ]);

  const adjacentClient = firstMatchAny(rawOutput, [
    /###\s*Identified\s+Adjacent\s+Client:\s*([^\n]+)/i,
    /Identified\s+Adjacent\s+Client:\s*([^\n]+)/i
  ]);

  // Main Email Copy: everything after marker
  let mainCopy = '';
  const mainCopyMatch = rawOutput.match(/(?:\*\*Main Email Copy:\*\*|Main Email Copy:)\s*\n([\s\S]*)$/i);
  if (mainCopyMatch && mainCopyMatch[1]) {
    mainCopy = mainCopyMatch[1].trim();
  }

  // Fallback to first Dear block
  if (!mainCopy) {
    const dearIdx = rawOutput.search(/\nDear\s+[^\n]+,\s*\n/i);
    if (dearIdx >= 0) mainCopy = rawOutput.slice(dearIdx).trim();
  }

  // Convert email copy to plain text (handles markdown too)
  const bodyCorePlain = markdownToPlainText(mainCopy || rawOutput);

  // Case details (only used if present)
  const details = extractCaseDetails(bodyCorePlain);

  // Preserve upstream recipient fields if already set, else use extracted
  const toEmail = fields.toEmail || item.json.toEmail || toEmailExtracted || '';
  const toName = fields.toName || item.json.toName || recipientName || '';

  // Subject. Preserve upstream subject if present, else extracted, else tribunal default
  const tribunalName = details.tribunal || 'Watford Employment Tribunal';
  const fallbackSubject = `${tribunalName}. ${details.caseNumber ? `Case ${details.caseNumber}` : 'Position statement'}. Respondent's position statement`;

  const subject = fields.subject || subjectExtracted || fallbackSubject;

  // If this is NOT a tribunal doc, we do not want the tribunal wrapper.
  // Heuristic: only add tribunal wrapper if we detect key markers.
  const looksLikeTribunalDoc =
    /CASE\s*NUMBER:/i.test(bodyCorePlain) ||
    /EMPLOYMENT\s+TRIBUNAL/i.test(bodyCorePlain) ||
    /\(\s*Claimant\s*\)/i.test(bodyCorePlain) ||
    /\(\s*Respondent\s*\)/i.test(bodyCorePlain);

  let bodyText = bodyCorePlain;

  if (looksLikeTribunalDoc) {
    const caseLine = details.caseNumber ? `Case number: ${details.caseNumber}` : 'Case number:';
    const partiesLine =
      (details.claimant || details.respondent)
        ? `${details.claimant || 'Claimant'} v ${details.respondent || 'Respondent'}`
        : 'Parties:';

    const greeting = toName ? `Dear ${toName},` : 'Dear Sir or Madam,';

    const intro =
`${greeting}

Re: ${tribunalName}
${caseLine}
${partiesLine}

Please find set out below the Respondent's position statement, provided pursuant to the Tribunal's Order requiring the Respondent to set out what it seeks to achieve to resolve these proceedings by agreement.

`;

    const signoff =
`\n\nYours faithfully,\n\nOmny Law\nFor and on behalf of the Respondent`;

    bodyText = `${intro}${bodyCorePlain}${signoff}`.replace(/\n{3,}/g, '\n\n').trim();
  }

  // Update flat fields for downstream nodes
  fields.toEmail = toEmail;
  fields.toName = toName;
  fields.subject = subject;
  fields.bodyText = bodyText;
  fields.bodyHtml = fields.bodyHtml || ''; // keep key present for compatibility

  const extracted = {
    adjacentClient: adjacentClient || fields.adjacentClient || '',
    recipientTitle: recipientTitle || fields.recipientTitle || '',
    source: 'agent_output_text'
  };

  // Graph-style draft message object (plain text)
  const draft = {
    subject,
    body: {
      contentType: 'Text',
      content: bodyText
    },
    toRecipients: toEmail
      ? [{ emailAddress: { address: toEmail, name: toName || undefined } }]
      : []
  };

  results.push({
    json: {
      draft,
      fields,
      extracted,
      raw: { output: rawOutput }
    }
  });
}

return results;

/**
 * n8n Code node (JavaScript)
 * Purpose: Take an agent "output" string and return a structured JSON payload
 *          suitable for a later Outlook draft email step (Graph-style + simple fields).
 *
 * Works with BOTH formats:
 *  - Markdown style: **Subject:**, **Recipient Name:**, **Recipient Email:**, **Main Email Copy:**
 *  - Plain style: Subject:, Recipient's Name:, Recipient's Email:, Main Email Copy:
 */

function asString(v) {
  if (v === null || v === undefined) return '';
  if (typeof v === 'string') return v;
  try { return JSON.stringify(v); } catch (e) { return String(v); }
}

function cleanLine(s) {
  return asString(s)
    .replace(/\r\n/g, '\n')
    .replace(/\r/g, '\n')
    .trim();
}

function stripBackticks(s) {
  return asString(s).replace(/`+/g, '').trim();
}

function firstMatch(text, regex) {
  const m = text.match(regex);
  return m && m[1] ? m[1].trim() : '';
}

function firstMatchAny(text, regexList) {
  for (const re of regexList) {
    const v = firstMatch(text, re);
    if (v) return v;
  }
  return '';
}

function normaliseEmail(raw) {
  let s = stripBackticks(raw || '');
  // Remove anything in brackets after the address
  s = s.replace(/\s*\(.*?\)\s*$/g, '').trim();
  // Pull first email-like token if there is extra text
  const m = s.match(/[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}/i);
  return m ? m[0] : s;
}

function toParagraphs(text) {
  const t = cleanLine(text);
  if (!t) return [];
  return t.split(/\n\s*\n/g).map(p => p.trim()).filter(Boolean);
}

function escapeHtml(s) {
  return asString(s)
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;')
    .replace(/"/g, '&quot;')
    .replace(/'/g, '&#39;');
}

function textToHtmlBody(mainCopyText, recipientName) {
  const raw = cleanLine(mainCopyText);

  // Optionally align greeting with extracted recipient name
  let aligned = raw;
  if (recipientName) {
    const firstName = recipientName.trim().split(/\s+/)[0] || recipientName.trim();
    aligned = aligned.replace(/^Dear\s+[^,\n]+,/i, `Dear ${firstName},`);
  }

  // Try to split signature from body using common sign-offs
  const signoffs = [
    /(\n\s*(Best regards|Kind regards|Warm regards|Regards|Sincerely|Yours sincerely|Yours faithfully)\s*,\s*\n)([\s\S]*)$/i
  ];

  let bodyPart = aligned;
  let signoffLine = '';
  let signaturePart = '';

  for (const re of signoffs) {
    const m = aligned.match(re);
    if (m) {
      bodyPart = aligned.slice(0, m.index).trim();
      signoffLine = (m[2] || '').trim();
      signaturePart = (m[3] || '').trim();
      break;
    }
  }

  const bodyParas = toParagraphs(bodyPart);
  const sigLines = cleanLine(signaturePart).split('\n').map(l => l.trim()).filter(Boolean);

  const bodyHtml = bodyParas
    .map(p => `<p style="margin:0 0 14px 0;font-size:14px;line-height:20px;color:#1f2937;">${escapeHtml(p)}</p>`)
    .join('');

  let signatureHtml = '';
  if (signoffLine || sigLines.length) {
    const sigName = sigLines[0] ? escapeHtml(sigLines[0]) : '';
    const sigRest = sigLines.slice(1).map(l => escapeHtml(l)).join('<br/>');

    signatureHtml = `
      <p style="margin:18px 0 8px 0;font-size:14px;line-height:20px;color:#1f2937;">${escapeHtml(signoffLine || 'Regards')},</p>
      <p style="margin:0;font-size:14px;line-height:20px;color:#1f2937;">
        <strong>${sigName}</strong>${sigRest ? `<br/>${sigRest}` : ''}
      </p>
    `.trim();
  }

  const preheaderText = 'Quick intro and a short meeting request.';

  const html = `
<!doctype html>
<html>
<head>
  <meta charset="utf-8">
  <meta name="viewport" content="width=device-width,initial-scale=1">
  <meta name="x-apple-disable-message-reformatting">
</head>
<body style="margin:0;padding:0;background-color:#f3f4f6;">
  <div style="display:none;font-size:1px;line-height:1px;max-height:0;max-width:0;opacity:0;overflow:hidden;">
    ${escapeHtml(preheaderText)}
  </div>

  <table role="presentation" cellpadding="0" cellspacing="0" border="0" width="100%" style="background-color:#f3f4f6;">
    <tr>
      <td align="center" style="padding:24px 12px;">
        <table role="presentation" cellpadding="0" cellspacing="0" border="0" width="640" style="width:640px;max-width:640px;background-color:#ffffff;border-radius:12px;overflow:hidden;border:1px solid #e5e7eb;">
          <tr>
            <td style="padding:18px 20px;background-color:#0b1220;">
              <div style="font-family:Segoe UI, Arial, sans-serif;font-size:14px;line-height:18px;color:#ffffff;font-weight:600;">
                Legal Engine
              </div>
              <div style="font-family:Segoe UI, Arial, sans-serif;font-size:12px;line-height:16px;color:#cbd5e1;margin-top:4px;">
                Practical AI for BD, experience capture, and operational efficiency
              </div>
            </td>
          </tr>

          <tr>
            <td style="padding:22px 20px 8px 20px;font-family:Segoe UI, Arial, sans-serif;">
              ${bodyHtml}
              ${signatureHtml}
            </td>
          </tr>

          <tr>
            <td style="padding:14px 20px 18px 20px;">
              <div style="font-family:Segoe UI, Arial, sans-serif;font-size:12px;line-height:16px;color:#6b7280;">
                If helpful, reply with a couple of times next week and I will send an invite.
              </div>
            </td>
          </tr>
        </table>

        <div style="font-family:Segoe UI, Arial, sans-serif;font-size:11px;line-height:14px;color:#9ca3af;margin-top:10px;">
          Sent via n8n automation
        </div>
      </td>
    </tr>
  </table>
</body>
</html>
  `.trim();

  return html;
}

function buildPlainText(mainCopyText, recipientName) {
  let t = cleanLine(mainCopyText);

  if (recipientName) {
    const firstName = recipientName.trim().split(/\s+/)[0] || recipientName.trim();
    t = t.replace(/^Dear\s+[^,\n]+,/i, `Dear ${firstName},`);
  }

  return t;
}

const results = [];

for (const item of items) {
  const rawOutput = cleanLine(item.json?.output);

  // Accept straight apostrophe and curly apostrophe
  const apos = "['’]";

  const adjacentClient = firstMatchAny(rawOutput, [
    /###\s*Identified\s+Adjacent\s+Client:\s*([^\n]+)/i,
    /Identified\s+Adjacent\s+Client:\s*([^\n]+)/i
  ]);

  const recipientName = firstMatchAny(rawOutput, [
    /\*\*Recipient Name:\*\*\s*([^\n]+)/i,
    new RegExp(`^\\s*Recipient${apos}?s?\\s*Name:\\s*(.+)$`, 'im'),
    /^\s*Recipient Name:\s*(.+)$/im
  ]);

  const recipientTitle = firstMatchAny(rawOutput, [
    /\*\*Recipient Title:\*\*\s*([^\n]+)/i,
    new RegExp(`^\\s*Recipient${apos}?s?\\s*Title:\\s*(.+)$`, 'im'),
    /^\s*Recipient Title:\s*(.+)$/im
  ]);

  const suggestedEmail = firstMatchAny(rawOutput, [
    /\*\*Suggested Email Address:\*\*\s*([^\n]+)/i,
    /^\s*Suggested Email Address:\s*(.+)$/im
  ]);

  const recipientEmailLine = firstMatchAny(rawOutput, [
    /\*\*Recipient Email:\*\*\s*([^\n]+)/i,
    new RegExp(`^\\s*Recipient${apos}?s?\\s*Email:\\s*(.+)$`, 'im'),
    /^\s*Recipient Email:\s*(.+)$/im
  ]) || suggestedEmail;

  const toEmail = normaliseEmail(recipientEmailLine);

  const subject = firstMatchAny(rawOutput, [
    /\*\*Subject:\*\*\s*([^\n]+)/i,
    /^\s*Subject:\s*(.+)$/im
  ]);

  // Main Email Copy: everything after marker
  let mainCopy = '';
  const mainCopyMatch = rawOutput.match(/(?:\*\*Main Email Copy:\*\*|Main Email Copy:)\s*\n([\s\S]*)$/i);
  if (mainCopyMatch && mainCopyMatch[1]) {
    mainCopy = mainCopyMatch[1].trim();
  }

  // Fallback to first Dear block
  if (!mainCopy) {
    const dearIdx = rawOutput.search(/\nDear\s+[^\n]+,\s*\n/i);
    if (dearIdx >= 0) mainCopy = rawOutput.slice(dearIdx).trim();
  }

  const bodyHtml = textToHtmlBody(mainCopy, recipientName);
  const bodyText = buildPlainText(mainCopy, recipientName);

  const message = {
    subject: subject || '(no subject)',
    body: {
      contentType: 'HTML',
      content: bodyHtml
    },
    toRecipients: toEmail
      ? [{ emailAddress: { address: toEmail, name: recipientName || undefined } }]
      : []
  };

  const fields = {
    toEmail: toEmail || '',
    toName: recipientName || '',
    subject: subject || '',
    bodyHtml,
    bodyText
  };

  const extracted = {
    adjacentClient: adjacentClient || '',
    recipientTitle: recipientTitle || '',
    source: 'agent_output_text'
  };

  results.push({
    json: {
      draft: message,
      fields,
      extracted,
      raw: { output: rawOutput }
    }
  });
}

return results;
