const email = $('Webhook').first().json.body.user_email.trim().toLowerCase();
const [fullNamePart, domain] = email.split("@");

function capitalize(str) {
  return str.charAt(0).toUpperCase() + str.slice(1);
}

// Extract names
let [firstName, lastName] = fullNamePart.split(/[._]/); // supports "john.doe" or "john_doe"
firstName = capitalize(firstName || "");
lastName = lastName ? capitalize(lastName) : "";
const fullName = lastName ? `${firstName} ${lastName}` : firstName;

// Extract company from domain
const publicSuffixes = ['co.uk', 'org.uk', 'ac.uk', 'gov.uk', 'com.au', 'com.br', 'co.in'];
let company = "";

const domainParts = domain.split(".");
const domainSuffix = domainParts.slice(-2).join(".");
const isTwoPartSuffix = publicSuffixes.includes(domainSuffix);

if (isTwoPartSuffix && domainParts.length > 2) {
  company = domainParts[domainParts.length - 3];
} else if (domainParts.length > 1) {
  company = domainParts[domainParts.length - 2];
} else {
  company = domainParts[0];
}

company = capitalize(company);

return [
  {
    json: {
      email,
      firstName,
      lastName,
      fullName,
      domain,
      company,
    },
  },
];

