// Generate a mobile-friendly HTML day briefing from merged calendar data
// Accepts mixed shapes (either your merged event objects OR ai_event wrappers).
// Returns ONE item with { html, subject }.
//
// It handles:
// - time: ev.time.{startISO,endISO,display,timeZone,durationMinutes} OR flat startISO/endISO/timeZone
// - organizer: ev.organizer{name,email} OR organizerName/organizerEmail
// - attendees: ev.attendeesAll / attendeesInternal / attendeesExternal OR single-attendee fields
// - external bios: ev.externalAttendeeBios OR attendees with `bio`
// - location: ev.location (string or {name})
// - links: ev.meetingLink / ev.webLink
//
// Sorts meetings by start time and prints: time range, subject (linked), location/link, organizer,
// internal & external attendees, and per-external bio (trimmed to ~120 words).

//////////////////// helpers ////////////////////
function clean(s){ return typeof s === 'string' ? s.trim() : ''; }
function lower(s){ return typeof s === 'string' ? s.toLowerCase() : ''; }
function get(o, p, d){
  var parts = p.split('.'); var cur = o;
  for (var i=0;i<parts.length;i++){ var k = parts[i]; if (cur && typeof cur==='object' && (k in cur)) cur = cur[k]; else return d; }
  return cur;
}
function domainFromEmail(email){ var e = lower(clean(email)); var at = e.indexOf('@'); return at>-1 ? e.slice(at+1) : ''; }
function normalizeIso(s){
  s = clean(s); if (!s) return null;
  var t = s.replace(/Z$/,''); var dot = t.indexOf('.'); if (dot>-1) t = t.slice(0, dot);
  if (t.length >= 19) t = t.slice(0,19); // YYYY-MM-DDTHH:MM:SS
  return t;
}
function hhmm(iso){ return (iso && iso.length>=16) ? iso.slice(11,16) : ''; }
function ymd(iso){ return (iso && iso.length>=10) ? iso.slice(0,10) : null; }
function parseUTCDate(iso){ // treat ISO (without Z) as UTC for header formatting
  if (!iso) return null;
  try { return new Date(iso + 'Z'); } catch(_) { return null; }
}
function headerDateString(date){
  // Friday 30 Aug 2025
  var DAYS = ['Sunday','Monday','Tuesday','Wednesday','Thursday','Friday','Saturday'];
  var MOS  = ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'];
  if (!date) date = new Date();
  var d = date.getUTCDate();
  var dow = DAYS[date.getUTCDay()];
  var mo = MOS[date.getUTCMonth()];
  var yr = date.getUTCFullYear();
  return dow + ' ' + String(d).padStart(2,'0') + ' ' + mo + ' ' + yr;
}
function wordsClamp(text, maxWords){
  var ws = clean(text).split(/\s+/).filter(Boolean);
  if (ws.length <= maxWords) return ws.join(' ');
  return ws.slice(0, maxWords).join(' ') + '‚Ä¶';
}
function uniqBy(arr, keyFn){
  var seen = {}; var out = [];
  for (var i=0;i<arr.length;i++){
    var k = keyFn(arr[i]);
    if (k && !seen[k]){ seen[k] = 1; out.push(arr[i]); }
    if (!k) out.push(arr[i]);
  }
  return out;
}

function deriveTimes(ev){
  var t = ev.time || {};
  var startISO = normalizeIso(t.startISO || ev.startISO || get(ev,'start.dateTime', null) || (typeof ev.start === 'string' ? ev.start : null));
  var endISO   = normalizeIso(t.endISO   || ev.endISO   || get(ev,'end.dateTime',   null) || (typeof ev.end   === 'string' ? ev.end   : null));
  var tz = t.timeZone || ev.timeZone || get(ev,'start.timeZone','UTC');
  var display = t.display || (ev.display || (ev.isAllDay ? 'All day' : (hhmm(startISO) + ((startISO && endISO)?'-':'') + hhmm(endISO))));
  var durationMinutes = (typeof t.durationMinutes === 'number') ? t.durationMinutes : (typeof ev.durationMinutes === 'number' ? ev.durationMinutes : null);
  return { startISO:startISO, endISO:endISO, display:display, timeZone:tz, durationMinutes:durationMinutes };
}
function normalizeAttendee(a){
  a = a || {};
  var email = clean(a.email || get(a,'emailAddress.address',''));
  var name  = clean(a.name  || get(a,'emailAddress.name','') || (email ? email.split('@')[0] : ''));
  var domain = clean(a.domain || domainFromEmail(email));
  var response = clean(a.response || get(a,'status.response','') || '');
  var bio = clean(a.bio || a.output || '');
  return { name:name, email:email, domain:domain, response:response, bio:bio };
}
function summarizeAttendeeList(list){
  if (!list || !list.length) return '‚Äî';
  return list.map(function(a){ return clean(a.name || a.email || ''); }).filter(Boolean).join(', ');
}
function eventKey(ev){
  var id = ev.id || ev.eventId || '';
  var s = get(ev,'time.startISO', ev.startISO || '') || '';
  var subj = clean(ev.subject || ev.Subject || '');
  return (id ? 'id:'+id : 'k:'+subj+'|'+s);
}

//////////////////// collect & normalize ////////////////////
var rows = $input.all().map(function(i){ return i.json; });

// If upstream produced ai_event wrapper, unwrap to a consistent event shape
var events = rows.map(function(x){
  return x && x.ai_event ? x.ai_event : x;
});

// Aggregate in case upstream still sends per-attendee items
var grouped = {};
for (var i=0;i<events.length;i++){
  var ev = events[i] || {};
  var key = eventKey(ev);
  if (!grouped[key]){
    grouped[key] = {
      id: ev.id || ev.eventId || null,
      subject: clean(ev.subject || ev.Subject || '(No subject)'),
      isAllDay: !!ev.isAllDay,
      time: deriveTimes(ev),
      organizer: {
        name: clean(get(ev,'organizer.name', ev.organizerName || '')),
        email: clean(get(ev,'organizer.email', ev.organizerEmail || ''))
      },
      internalDomain: clean(ev.internalDomain || domainFromEmail(get(ev,'organizer.email', ev.organizerEmail || ''))),
      location: (get(ev,'location.name', null) || (typeof ev.location==='string' ? ev.location : null)) || '',
      meetingLink: ev.meetingLink || null,
      webLink: ev.webLink || null,
      description: clean(ev.contextHint || ev.description || ev.bodyPreview || ''),
      attendeesAll: [],
      externalAttendeeBios: Array.isArray(ev.externalAttendeeBios) ? ev.externalAttendeeBios.slice() : []
    };
  }
  // Merge attendees from any shape
  if (Array.isArray(ev.attendeesAll) && ev.attendeesAll.length){
    grouped[key].attendeesAll = grouped[key].attendeesAll.concat(ev.attendeesAll.map(normalizeAttendee));
  } else if (Array.isArray(ev.attendees) && ev.attendees.length){
    grouped[key].attendeesAll = grouped[key].attendeesAll.concat(ev.attendees.map(normalizeAttendee));
  } else if (ev.attendeeEmail || ev.attendeeName){
    grouped[key].attendeesAll.push(normalizeAttendee({ email: ev.attendeeEmail, name: ev.attendeeName, domain: ev.attendeeDomain, response: ev.attendeeResponse, bio: ev.bio }));
  }
  // Merge bios if present on attendeesExternal
  if (Array.isArray(ev.attendeesExternal)){
    for (var j=0;j<ev.attendeesExternal.length;j++){
      var a = normalizeAttendee(ev.attendeesExternal[j]);
      if (a.bio) grouped[key].externalAttendeeBios.push({ name:a.name||a.email, email:a.email||'', domain:a.domain||'', bio:a.bio });
    }
  }
}

// Deduplicate attendees & bios, split internal/external
var meetings = [];
for (var k in grouped){
  var g = grouped[k];
  g.attendeesAll = uniqBy(g.attendeesAll, function(a){ return lower(a.email); });
  var internal = []; var external = [];
  for (var i2=0;i2<g.attendeesAll.length;i2++){
    var a2 = g.attendeesAll[i2];
    if (a2.domain && g.internalDomain && lower(a2.domain) === lower(g.internalDomain)) internal.push(a2);
    else external.push(a2);
  }
  // Normalize bios: prefer explicit externalAttendeeBios, else from external attendees
  var bios = [];
  if (Array.isArray(g.externalAttendeeBios) && g.externalAttendeeBios.length){
    for (var b=0;b<g.externalAttendeeBios.length;b++){
      var eb = g.externalAttendeeBios[b] || {};
      if (clean(eb.bio)) bios.push({ name: clean(eb.name||eb.email||''), email: clean(eb.email||''), domain: clean(eb.domain||''), bio: clean(eb.bio) });
    }
  } else {
    for (var e=0;e<external.length;e++){
      var ex = external[e];
      if (ex.bio) bios.push({ name: ex.name||ex.email||'', email: ex.email||'', domain: ex.domain||'', bio: ex.bio });
    }
  }
  // trim bios to ~120 words
  for (var bb=0;bb<bios.length;bb++){ bios[bb].bio = wordsClamp(bios[bb].bio, 120); }

  meetings.push({
    id: g.id, subject: g.subject, isAllDay: g.isAllDay, time: g.time,
    organizer: g.organizer, internalDomain: g.internalDomain,
    location: g.location, meetingLink: g.meetingLink, webLink: g.webLink,
    description: g.description,
    attendeesInternal: internal, attendeesExternal: external,
    bios: bios
  });
}

// Sort by start time
meetings.sort(function(a,b){
  var as = a.time.startISO || ''; var bs = b.time.startISO || '';
  return as < bs ? -1 : (as > bs ? 1 : 0);
});

// Header date from earliest meeting start, fallback to today
var headerDate = (meetings[0] && meetings[0].time && meetings[0].time.startISO) ? parseUTCDate(meetings[0].time.startISO) : new Date();
var headerText = headerDateString(headerDate);

// Build HTML
function esc(s){
  return String(s==null?'':s).replace(/&/g,'&amp;').replace(/</g,'&lt;').replace(/>/g,'&gt;');
}
function linkify(label, href){
  if (!href) return esc(label);
  return '<a href="'+esc(href)+'" target="_blank" rel="noopener noreferrer">'+esc(label)+'</a>';
}

var htmlParts = [];
htmlParts.push(
'<!doctype html><html><head><meta charset="utf-8"><meta name="viewport" content="width=device-width,initial-scale=1">',
'<title>Start-of-day Briefing ‚Äî '+esc(headerText)+'</title>',
'<style>',
'body{margin:0;font-family:-apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Helvetica,Arial,sans-serif;color:#111;background:#fafafa;}',
'.wrap{max-width:720px;margin:0 auto;padding:16px;}',
'.hdr{position:sticky;top:0;background:#fff;padding:12px 16px;margin:0 -16px 16px;border-bottom:1px solid #eee;z-index:10;}',
'.hdr h1{font-size:20px;margin:0;}',
'.mtg{background:#fff;border:1px solid #eee;border-radius:12px;padding:14px 14px 12px;margin:12px 0;box-shadow:0 1px 1px rgba(0,0,0,0.02);}',
'.row{display:flex;gap:8px;align-items:center;flex-wrap:wrap;color:#333;font-size:13px;}',
'.time{font-weight:600;color:#0b5cff;}',
'.subj{font-size:16px;font-weight:700;margin:4px 0 6px 0;}',
'.meta{color:#555;font-size:13px;margin:2px 0 8px 0;}',
'.pill{display:inline-block;background:#f1f5ff;color:#0b5cff;border-radius:999px;padding:2px 8px;font-size:12px;margin:2px 6px 0 0;}',
'.bios{border-top:1px solid #f0f0f0;margin-top:10px;padding-top:10px;}',
'.bio{margin:8px 0;}',
'.bio .name{font-weight:600;}',
'a{color:#0b5cff;text-decoration:none} a:hover{text-decoration:underline}',
'</style></head><body><div class="wrap">',
'<div class="hdr"><h1>Start-of-day Briefing ‚Äî '+esc(headerText)+'</h1></div>'
);

if (!meetings.length){
  htmlParts.push('<p>No meetings found for this day.</p>');
} else {
  for (var i3=0;i3<meetings.length;i3++){
    var m = meetings[i3];
    var t = m.time || {};
    var timeLabel = m.isAllDay ? 'All day' : ((t.display || ((hhmm(t.startISO)||'') + ((t.startISO&&t.endISO)?'-':'') + (hhmm(t.endISO)||''))) + (t.timeZone ? ' '+t.timeZone : ''));
    var subjectLine = esc(m.subject);
    var href = m.meetingLink || m.webLink || '';
    var subjectHtml = href ? linkify(subjectLine, href) : subjectLine;
    var loc = m.location ? esc(m.location) : '';
    var org = (m.organizer && (m.organizer.name || m.organizer.email)) ? esc([m.organizer.name, m.organizer.email ? '<'+m.organizer.email+'>' : ''].filter(Boolean).join(' ')) : '';
    var internalList = summarizeAttendeeList(m.attendeesInternal);
    var externalList = summarizeAttendeeList(m.attendeesExternal);

    htmlParts.push('<section class="mtg">');
    htmlParts.push('<div class="row"><span class="time">'+esc(timeLabel)+'</span></div>');
    htmlParts.push('<div class="subj">'+subjectHtml+'</div>');
    var metaBits = [];
    if (loc) metaBits.push('üìç '+loc);
    if (org) metaBits.push('üë§ '+org);
    if (t.durationMinutes) metaBits.push('‚è± '+t.durationMinutes+' min');
    if ((m.meetingLink || m.webLink) && !href){
      var linksLine = [m.meetingLink, m.webLink].filter(Boolean).map(function(u){ return linkify('link', u); }).join(' ¬∑ ');
      metaBits.push('üîó '+linksLine);
    }
    if (metaBits.length) htmlParts.push('<div class="meta">'+metaBits.join(' &nbsp;¬∑&nbsp; ')+'</div>');

    htmlParts.push('<div class="meta"><span class="pill">Internal</span> '+esc(internalList)+'</div>');
    htmlParts.push('<div class="meta"><span class="pill">External</span> '+esc(externalList)+'</div>');

    if (m.bios && m.bios.length){
      htmlParts.push('<div class="bios"><div class="meta" style="margin-bottom:6px;">Background</div>');
      for (var b2=0;b2<m.bios.length;b2++){
        var b = m.bios[b2];
        var nm = esc(b.name || b.email || '');
        var email = b.email ? ' &lt;'+esc(b.email)+'&gt;' : '';
        var dom = b.domain ? ' ‚Äî '+esc(b.domain) : '';
        var bioTxt = esc(b.bio);
        htmlParts.push('<div class="bio"><div class="name">', nm, email, dom, '</div><div class="txt">', bioTxt, '</div></div>');
      }
      htmlParts.push('</div>');
    }
    htmlParts.push('</section>');
  }
}

htmlParts.push('</div></body></html>');
var html = htmlParts.join('');

var subjOut = 'Start-of-day Briefing ‚Äî ' + headerText;
return [{ json: { html: html, subject: subjOut, meetingCount: meetings.length } }];

