  var rawA = Array.isArray(ev.attendeesAll) ? ev.attendeesAll : (Array.isArray(ev.attendees) ? ev.attendees : []);
  var att = rawA.map(normAttendee).filter(function(a){ return a.email || a.name; }).filter(function(a){ return !isNoiseAttendee(a); });
  att = uniqBy(att, function(a){ return lower(a.email); });

  var split = splitAttendees(att, internalDomain);

  // Trim description, use first line as hint
  var desc = clean(ev.description || ev.bodyPreview || '');
  var descLine = firstLine(desc);
  var descTrim = clipWords(desc, 90); // ~90 words max for hint

  var subject = clean(ev.subject || ev.Subject || '(No subject)');
  var dateISO = times.startISO ? times.startISO.slice(0,10) : null;

  var externalBios = (Array.isArray(ev.externalAttendeeBios) ? ev.externalAttendeeBios : [])
    .map(function(b){
      return { name: clean(b.name||''), email: clean(b.email||''), domain: clean(b.domain||''), bio: clipWords(clean(b.bio||''), 120) };
    });

  var ai_event = prune({
    id: ev.id || ev.eventId || null,
    subject: subject,
    isAllDay: !!ev.isAllDay,
    date: dateISO,
    time: {
      startISO: times.startISO,
      endISO: times.endISO,
      timeZone: times.timeZone,
      display: times.display,
      startEpoch: times.startEpoch,
      endEpoch: times.endEpoch,
      durationMinutes: times.durationMinutes
    },
    organizer: organizer,
    location: (get(ev,'location.name', null) || (typeof ev.location==='string' ? ev.location : null)) || null,
    meetingLink: ev.meetingLink || null,
    webLink: ev.webLink || null,
    contextHint: descTrim || null,
    attendees: {
      internal: split.internal.map(function(a){ return { name:a.name, email:a.email, domain:a.domain, response:a.response }; }),
      external: split.external.map(function(a){ return { name:a.name, email:a.email, domain:a.domain, response:a.response, bio: a.bio ? clipWords(a.bio, 120) : undefined }; })
    },
    externalBios: externalBios.length ? externalBios : undefined
  });

  // Compact text prompt for LLMs that prefer plain text
  var ai_text = [
    'Meeting: ' + subject,
    'Date: ' + (dateISO || 'N/A') + '  Time: ' + (times.display || 'N/A') + ' ' + (times.timeZone || 'UTC'),
    organizer.name || organizer.email ? ('Organizer: ' + clean([organizer.name, organizer.email ? '<'+organizer.email+'>' : ''].filter(Boolean).join(' '))) : null,
    ev.location && (get(ev,'location.name',null) || typeof ev.location==='string') ? ('Location: ' + (get(ev,'location.name',null) || ev.location)) : null,
    (ev.meetingLink || ev.webLink) ? ('Links: ' + [ev.meetingLink, ev.webLink].filter(Boolean).join(' | ')) : null,
    split.internal.length ? ('Internal: ' + split.internal.map(function(a){ return a.name || a.email; }).join(', ')) : null,
    split.external.length ? ('External: ' + split.external.map(function(a){ return a.name || a.email; }).join(', ')) : null,
    externalBios.length ? ('External bios:\n- ' + externalBios.map(function(b){ return (b.name || b.email) + ' ‚Äî ' + b.bio; }).join('\n- ')) : null,
    descLine ? ('Context: ' + descLine) : null
  ].filter(Boolean).join('\n');

  out.push({ json: { ai_event: ai_event, ai_text: ai_text } });
}

if (out.length === 0){
  return [{ json: { warning: 'No calendar items to clean. Check upstream node output.' } }];
}

