// Edit if needed:
const INTERNAL_DOMAINS = ["legalengine.co.uk"];

// ---------- helpers ----------
function clean(s){ return (typeof s === 'string' ? s.trim() : (s==null ? '' : String(s).trim())); }
function lower(s){ return typeof s === 'string' ? s.toLowerCase() : ''; }
function get(o, path, d){
  var parts = path.split('.'); var cur = o;
  for (var i=0;i<parts.length;i++){ var k=parts[i]; if (cur && typeof cur==='object' && (k in cur)) cur = cur[k]; else return d; }
  return cur;
}
function domainFromEmail(email){ var e = lower(clean(email)); var i = e.indexOf('@'); return i>-1 ? e.slice(i+1) : ''; }
function isoFromEpochSec(sec){
  if (typeof sec !== 'number') return null;
  var d=new Date(sec*1000), yyyy=d.getUTCFullYear(), mm=String(d.getUTCMonth()+1).padStart(2,'0'), dd=String(d.getUTCDate()).padStart(2,'0'),
      hh=String(d.getUTCHours()).padStart(2,'0'), mi=String(d.getUTCMinutes()).padStart(2,'0'), ss=String(d.getUTCSeconds()).padStart(2,'0');
  return `${yyyy}-${mm}-${dd}T${hh}:${mi}:${ss}`;
}
function normalizeIso(s){
  s = clean(s); if (!s) return null;
  var t = s.replace(/Z$/,''); var dot = t.indexOf('.');
  if (dot>-1) t = t.slice(0,dot);
  if (t.length>=19) t = t.slice(0,19); // YYYY-MM-DDTHH:MM:SS
  return t;
}
function startISOFromEvent(ev){
  var s1 = get(ev,'start.dateTime',null); if (s1) return normalizeIso(String(s1));
  var s2 = ev.startISO || get(ev,'start',null); if (typeof s2 === 'string') return normalizeIso(s2);
  var e  = (typeof ev.epoch === 'number') ? ev.epoch : get(ev,'start.epoch',null);
  if (typeof e === 'number') return normalizeIso(isoFromEpochSec(e));
  return null;
}
function endISOFromEvent(ev){
  var e1 = get(ev,'end.dateTime',null); if (e1) return normalizeIso(String(e1));
  var e2 = ev.endISO || get(ev,'end',null); if (typeof e2 === 'string') return normalizeIso(e2);
  var ee = get(ev,'end.epoch',null);
  if (typeof ee === 'number') return normalizeIso(isoFromEpochSec(ee));
  return null;
}
function titleCase(s){ return String(s||'').split(/\s+|-/).filter(Boolean).map(w=>w.charAt(0).toUpperCase()+w.slice(1)).join(' '); }
function inferCompanyFromDomain(domain){
  domain = lower(clean(domain||'')); if (!domain) return '';
  const IGNORE={'gmail.com':1,'googlemail.com':1,'yahoo.com':1,'hotmail.com':1,'outlook.com':1,'live.com':1,'msn.com':1,'icloud.com':1,'me.com':1,'proton.me':1,'protonmail.com':1,'gmx.com':1,'aol.com':1};
  if (IGNORE[domain]) return '';
  var d = domain.replace(/^(mail|owa|webmail|mx|smtp|imap|app|portal|m|eu|us)\./,'');
  var multi=['co.uk','com.au','com.br','com.ar','com.mx','co.jp','co.in','com.sg','com.hk','com.tr','com.cn','co.nz'];
  for (var i=0;i<multi.length;i++){ var t=multi[i]; if (d.endsWith('.'+t)){ var base=d.slice(0,-(t.length+1)); var p=base.split('.'); return titleCase(p[p.length-1]||base); } }
  var parts=d.split('.'); if (parts.length>=2) return titleCase(parts[parts.length-2]); return titleCase(d);
}
function isNoiseAttendee(nameLower, emailLower, typeLower){
  return typeLower==='resource' || nameLower.indexOf('room')>-1 || emailLower.indexOf('resource')>-1 ||
         emailLower.indexOf('calendar')>-1 || emailLower.indexOf('no-reply')===0 || emailLower.indexOf('noreply')===0;
}
function getSubject(ev){
  var candidates = ['meetingSubject','subject','Subject','title','name','summary'];
  for (var i=0;i<candidates.length;i++){
    var v = ev[candidates[i]];
    if (typeof v === 'string' && clean(v)) return clean(v);
  }
  return '(No subject)';
}

// ---------- inputs & internal domain inference ----------
var events = $input.all().map(i=>i.json||{});

var counts={}; 
for (var i=0;i<events.length;i++){
  var org=clean(get(events[i],'organizer.email','')||get(events[i],'organizer.emailAddress.address',''));
  var d=domainFromEmail(org); if(d) counts[d]=(counts[d]||0)+1;
}
var globalInternal=null,bestN=-1;
for (var k in counts){ if(counts[k]>bestN){ bestN=counts[k]; globalInternal=k; } }
var INTERNAL = new Set([].concat(INTERNAL_DOMAINS, globalInternal?[globalInternal]:[]).filter(Boolean).map(x=>lower(x)));

// ---------- emit one item per external attendee per meeting ----------
var out = [];

for (var j=0;j<events.length;j++){
  var ev = events[j] || {};
  var subject = getSubject(ev);
  var sISO = startISOFromEvent(ev);
  var eISO = endISOFromEvent(ev);

  var attendeesRaw = Array.isArray(ev.attendees) ? ev.attendees : [];
  var seen = {}; // de-dupe by email within this meeting

  for (var a=0;a<attendeesRaw.length;a++){
    var att = attendeesRaw[a] || {};
    var emailOrig = clean(att.email || get(att,'emailAddress.address',''));
    var email = lower(emailOrig);
    if (!email || email.indexOf('@')===-1) continue;

    var name = clean(att.name || get(att,'emailAddress.name','') || (emailOrig?emailOrig.split('@')[0]:''));
    var domain = domainFromEmail(emailOrig);
    var typeLower = lower(String(att.type||'')); var nameLower = lower(name);

    if (isNoiseAttendee(nameLower, email, typeLower)) continue;
    if (INTERNAL.has(domain)) continue;
    if (seen[email]) continue; seen[email]=1;

    out.push({ json: {
      meetingSubject: subject,
      startISO: sISO,
      endISO: eISO,
      name: name,
      email: emailOrig,
      domain: domain,
      company: inferCompanyFromDomain(domain)
    }});
  }
}

// Sort by meeting start time, then subject, then attendee name
out.sort((a,b)=>{
  const as=a.json.startISO||'', bs=b.json.startISO||'';
  if (as!==bs) return as<bs?-1:1;
  const ms=(a.json.meetingSubject||'').localeCompare(b.json.meetingSubject||'');
  if (ms!==0) return ms;
  return (a.json.name||'').localeCompare(b.json.name||'');
});

return out.length ? out : [{ json: { info: 'No external attendees found in input.' } }];

