parameters.jsCode:
/**
 * n8n Code node
 * Input: expects the markdown/PDF-extracted text in one of:
 *   - $json.text
 *   - $json.markdown
 *   - $json.output
 *
 * Output:
 *   - paragraph_1
 *   - paragraph_2
 *   - combined
 *   - found_marker
 *   - error (if any)
 */

const raw =
  ($json.text ?? $json.markdown ?? $json.output ?? '').toString();

function normaliseNewlines(s) {
  return s
    .replace(/\/n\s*/g, '\n') // handle literal "/n"
    .replace(/\r\n/g, '\n')
    .replace(/\r/g, '\n');
}

function findStartIndex(s) {
  const patterns = [
    /178\s*\nIdeas[\s\S]*?\nExtract[\s\S]*?\n/i,
    /Ideas[\s\S]*?\nExtract[\s\S]*?\n/i,
  ];

  for (const re of patterns) {
    const m = re.exec(s);
    if (m) return m.index + m[0].length;
  }
  return -1;
}

function shouldBreak(prevLine, nextLine) {
  if (!prevLine || !nextLine) return false;

  const prev = prevLine.trim();
  const next = nextLine.trim();
  const nextStartsUpperOrDigit = /^[A-Z0-9]/.test(next);

  // Strong signal. Question or exclamation often ends a paragraph in PDF text exports
  if (/[?!]$/.test(prev) && nextStartsUpperOrDigit && prev.length < 140) return true;

  // Secondary, conservative signal. Avoid over splitting inside normal prose
  if (/\.$/.test(prev) && nextStartsUpperOrDigit && prev.length < 60) return true;

  return false;
}

function extractParagraphs(afterText, maxNeeded = 2) {
  const paras = [];

  // First try blank line separation
  const blocks = afterText
    .split(/\n{2,}/)
    .map(b => b.trim())
    .filter(Boolean);

  for (const block of blocks) {
    if (paras.length >= maxNeeded) break;

    const lines = block
      .split('\n')
      .map(l => l.trim())
      .filter(l => l.length > 0);

    if (lines.length === 0) continue;

    let current = lines[0];

    for (let i = 1; i < lines.length; i++) {
      const prev = lines[i - 1];
      const next = lines[i];

      if (shouldBreak(prev, next) && paras.length < maxNeeded) {
        paras.push(current.replace(/\s+/g, ' ').trim());
        current = next;
        if (paras.length >= maxNeeded) break;
      } else {
        current += ' ' + next;
      }
    }

    if (paras.length < maxNeeded) {
      paras.push(current.replace(/\s+/g, ' ').trim());
    }
  }

  return paras.slice(0, maxNeeded);
}

try {
  const text = normaliseNewlines(raw);
  const startIdx = findStartIndex(text);

  if (startIdx === -1) {
    return [
      {
        json: {
          paragraph_1: '',
          paragraph_2: '',
          combined: '',
          found_marker: false,
          error: 'Marker not found. Tried patterns around "178\\nIdeas" and "Extract".',
        },
      },
    ];
  }

  const after = text.slice(startIdx).replace(/^\s+/, '');
  const [p1 = '', p2 = ''] = extractParagraphs(after, 2);

  return [
    {
      json: {
        paragraph_1: p1,
        paragraph_2: p2,
        combined: [p1, p2].filter(Boolean).join('\n\n'),
        found_marker: true,
        error: '',
      },
    },
  ];
} catch (e) {
  return [
    {
      json: {
        paragraph_1: '',
        paragraph_2: '',
        combined: '',
        found_marker: false,
        error: (e && e.message) ? e.message : String(e),
      },
    },
  ];
}

/**
 * n8n Code node
 * Goal: return ALL text after the LAST date-style header in the document.
 *
 * Date header patterns supported (examples):
 *  - 28/05/24 - Sales Campaigns (Part 4) – Opportunity knocks…
 *  - 22/12/25 – looking forwards
 *  - Family edition – 08/04/24
 *  - 18/03/24 - Business trips
 *
 * Input: expects the document text in one of:
 *   - $json.text
 *   - $json.markdown
 *   - $json.output
 *
 * Output:
 *   - last_header_line
 *   - last_header_index
 *   - text_after_last_header
 *   - found_header
 *   - error
 */

const raw =
  ($json.text ?? $json.markdown ?? $json.output ?? '').toString();

function normaliseNewlines(s) {
  return s
    .replace(/\/n\s*/g, '\n') // handle literal "/n"
    .replace(/\r\n/g, '\n')
    .replace(/\r/g, '\n');
}

// Recognises date headers in two common forms:
// A) Date at start of line: 28/05/24 - Something
// B) Date at end of line:  Family edition – 08/04/24
function isDateHeaderLine(line) {
  const l = line.trim();

  // A) starts with DD/MM/YY or DD/MM/YYYY
  const startsWithDate =
    /^(\d{1,2}\/\d{1,2}\/\d{2,4})\s*(?:[–—-]|:)\s*\S+/i.test(l);

  // Also allow "DD/MM/YY" followed by just whitespace then text (some PDFs lose the dash)
  const startsWithDateLoose =
    /^(\d{1,2}\/\d{1,2}\/\d{2,4})\s+\S+/i.test(l);

  // B) ends with a date, preceded by some words
  const endsWithDate =
    /\b(\d{1,2}\/\d{1,2}\/\d{2,4})\s*$/i.test(l) && /[A-Za-z]/.test(l);

  return startsWithDate || startsWithDateLoose || endsWithDate;
}

function getLastHeader(lines) {
  let lastIdx = -1;
  let lastLine = '';

  for (let i = 0; i < lines.length; i++) {
    if (isDateHeaderLine(lines[i])) {
      lastIdx = i;
      lastLine = lines[i].trim();
    }
  }
  return { lastIdx, lastLine };
}

try {
  const text = normaliseNewlines(raw);

  // Work line-by-line so we can find the last header occurrence reliably
  const lines = text.split('\n');

  const { lastIdx, lastLine } = getLastHeader(lines);

  if (lastIdx === -1) {
    return [
      {
        json: {
          last_header_line: '',
          last_header_index: -1,
          text_after_last_header: '',
          found_header: false,
          error: 'No date-style header line found (e.g. "28/05/24 - Title" or "Title – 08/04/24").',
        },
      },
    ];
  }

  // Return everything AFTER that header line
  const afterLines = lines.slice(lastIdx + 1);

  // Trim leading blank lines
  while (afterLines.length && afterLines[0].trim() === '') afterLines.shift();

  const afterText = afterLines.join('\n').trimEnd();

  return [
    {
      json: {
        last_header_line: lastLine,
        last_header_index: lastIdx,
        text_after_last_header: afterText,
        found_header: true,
        error: '',
      },
    },
  ];
} catch (e) {
  return [
    {
      json: {
        last_header_line: '',
        last_header_index: -1,
        text_after_last_header: '',
        found_header: false,
        error: (e && e.message) ? e.message : String(e),
      },
    },
  ];
}
