// Logs
console.log("Incoming JSON:", $json);

// Read inputs safely
const currentCalls = $json["Number of Calls"] ?? 0;
const lastCall = $json["Last Call Length (m)"] ?? 0;
const previousTotal = $json["Total Duration of calls (m)"] ?? 0;

// Increment call count
const nextCalls = currentCalls + 1;

// Update running total
const newTotal = previousTotal + lastCall;

// Debug logs
console.log("Next Calls:", nextCalls);
console.log("Last Call Length:", lastCall);
console.log("New Total Duration:", newTotal);

// Return only the three variables
return [
  {
    json: {
      "Number of Calls": nextCalls,
      "Last Call Length (m)": lastCall,
      "Total Duration of calls (m)": newTotal
    },
    pairedItem: { item: 0 }
  }
];
