// --- Function node code ---
// Get the first item (same pattern you used)
const inputDate = new Date($input.first().json.currentDate);   // "2025-05-26T10:22:34.868+01:00"

// Helper to zero-pad to two digits
const pad = n => n.toString().padStart(2, '0');

// Date  â†’  MM/DD/YYYY
const formattedDate = `${pad(inputDate.getMonth() + 1)}/${pad(inputDate.getDate())}/${inputDate.getFullYear()}`;

// Time  â†’  HH:mm:ss   (24-hour clock)
const formattedTime = `${pad(inputDate.getHours())}:${pad(inputDate.getMinutes())}:${pad(inputDate.getSeconds())}`;

return [
  {
    json: {
      formattedDate,   // e.g. "05/26/2025"
      formattedTime    // e.g. "09:22:34"
    }
  }
];

