// Process all incoming items
const items = $input.all();

function parseOutput(s) {
  if (typeof s !== 'string') return s;

  // Trim first
  let str = s.trim();

  // If it is a fenced code block like ```json\n...\n``` or ```\n...\n```
  const fenced = str.match(/^```[a-zA-Z0-9]*\n([\s\S]*?)\n```$/);
  if (fenced) {
    str = fenced[1].trim();
  }

  // 1) Try JSON, then maybe JSON again
  try {
    const once = JSON.parse(str);
    if (typeof once === 'string') {
      try {
        return JSON.parse(once);
      } catch {
        return once;
      }
    }
    return once;
  } catch (_) {}

  // 2) Fallback. parse sectioned freeform text with your headings
  const get = (label) => {
    const m = str.match(new RegExp(`${label}:\\n([\\s\\S]*?)(?:\\n\\n|$)`));
    return m ? m[1].trim() : '';
  };

  const formatsRaw = get('Potential Formats');
  const potentialFormats = formatsRaw
    .split('\n')
    .map(line => line.replace(/^[-•]\s*/, '').trim())
    .filter(Boolean);

  return {
    areaToExplore: get('Area to Explore'),
    potentialFormats,
    formattedFormatsHTML: potentialFormats.map(f => `<li>${f}</li>`).join(''),
    suggestedUse: get('Suggested Use'),
    somethingToThinkAbout: get('Something to Think About'),
  };
}

function normalize(parsed) {
  if (parsed && parsed.matter) {
    const { matter, submission_meta, recommendation } = parsed;
    const {
      is_confidential,
      D1,
      D2,
      D3,
      D4,
      D5,
      D6,
      D7,
      D8,
      D9,
    } = matter || {};

    return {
      matter,
      submission_meta,
      recommendation,
      is_confidential,
      D1,
      D2,
      D3,
      D4,
      D5,
      D6,
      D7,
      D8,
      D9,
      client: D1?.client_name ?? null,
      matterDescription: D2?.matter_description ?? null,
      matterValue: D3?.matter_value?.amount ?? null,
      matterCurrency: D3?.matter_value?.currency ?? null,
      jurisdictions: D4?.jurisdictions ?? [],
      leadLawyer: D5?.lead_lawyer?.name ?? null,
      startDate: D8?.start_date ?? null,
      completed: Boolean(D8?.completed),
      pressLinks: D9?.press_coverage_links ?? [],
    };
  }
  return parsed;
}

const results = [];

for (const item of items) {
  const incoming = { ...(item.json ?? {}) };

  // Keep everything from the prior node except the raw output string
  const passthrough = { ...incoming };
  delete passthrough.output;

  const raw = incoming.output;

  // If there is no output to parse, just pass the item through unchanged
  if (raw === undefined || raw === null) {
    results.push({ json: passthrough });
    continue;
  }

  const parsed = parseOutput(raw);
  const out = normalize(parsed);

  if (Array.isArray(out)) {
    for (const o of out) {
      results.push({ json: { ...passthrough, ...(o ?? {}) } });
    }
  } else if (out && typeof out === 'object') {
