// Get all items from the earlier Webhook node.
// Change "Webhook1" to the exact name of your webhook node, matching your workflow
const sourceItems = $items("Webhook1") ?? [];
if (sourceItems.length === 0) {
  return [];
}

// Helper to walk any object/array and normalise all "email" fields
function normaliseEmails(obj) {
  if (obj == null || typeof obj !== "object") {
    return;
  }

  if (Array.isArray(obj)) {
    for (const item of obj) {
      normaliseEmails(item);
    }
    return;
  }

  for (const key of Object.keys(obj)) {
    const value = obj[key];

    // If property name is "email" and is a string. trim and lowercase it
    if (key === "email" && typeof value === "string") {
      obj[key] = value.trim().toLowerCase();
    }

    // Recurse into nested objects and arrays
    if (value && typeof value === "object") {
      normaliseEmails(value);
    }
  }
}

return sourceItems.map(item => {
  const out = {
    ...item,
    json: { ...item.json },
  };

  normaliseEmails(out.json);

  return out;
});

