assignments.value:
={{
  (() => {
    const firm =
      $('Webhook1').first().json?.body?.data?.conversation_initiation_client_data?.dynamic_variables?.users_firm;

    const firmClean = (firm ?? '').toString().trim();
    if (firmClean) return firmClean;

    const email =
      $('email lowercase2').first().json?.body?.data?.conversation_initiation_client_data?.dynamic_variables?.email;

    const domain = ((email ?? '').toString().split('@')[1] ?? '').split('.')[0] ?? '';
    return domain.toString().trim();
  })()
}}

