parameters.jsCode:
/**
 * n8n Code node (robust JSON extraction)
 * Handles:
 *  - Input as array of items: [{ output: "```json\n{...}\n```" }]
 *  - output wrapped in markdown fences
 *  - output double-encoded as JSON string
 *  - extra text before/after JSON
 *  - literal "/n" delimiters
 *
 * Output:
 *  - parsed: the extracted JSON object (or null if it cannot be parsed)
 *  - raw: original raw string (trimmed)
 *  - clean: cleaned string after stripping fences (trimmed)
 *  - error: parse error (if any)
 */

function asString(v) {
  if (v === null || v === undefined) return '';
  if (typeof v === 'string') return v;
  try { return JSON.stringify(v); } catch { return String(v); }
}

function stripCodeFences(s) {
  // Remove leading/trailing triple backtick fences, optional language tag
  // Works even if there is whitespace around them
  return s
    .replace(/^\s*```[a-zA-Z0-9_-]*\s*\n?/m, '')
    .replace(/\n?\s*```\s*$/m, '');
}

function normaliseNewlines(s) {
  return s
    .replace(/\/n\s*/g, '\n')     // handle literal "/n" from some models
    .replace(/\r\n/g, '\n')
    .replace(/\r/g, '\n')
    .trim();
}

function tryParseJson(s) {
  // Attempt direct parse
  try { return { ok: true, value: JSON.parse(s) }; } catch (_) {}

  // If it is a JSON string that contains JSON, parse twice
  try {
    const once = JSON.parse(s);
    if (typeof once === 'string') {
      try { return { ok: true, value: JSON.parse(once) }; } catch (_) {}
    }
  } catch (_) {}

  return { ok: false, value: null };
}

function extractFirstJsonObject(s) {
  // Extract the first balanced {...} JSON object from a string.
  // This ignores braces inside strings properly.
  let start = -1;
  let depth = 0;
  let inString = false;
  let escape = false;

  for (let i = 0; i < s.length; i++) {
    const ch = s[i];

    if (escape) {
      escape = false;
      continue;
    }
    if (ch === '\\') {
      if (inString) escape = true;
      continue;
    }
    if (ch === '"') {
      inString = !inString;
      continue;
    }
    if (inString) continue;

    if (ch === '{') {
      if (depth === 0) start = i;
      depth++;
    } else if (ch === '}') {
      depth--;
      if (depth === 0 && start !== -1) {
        return s.slice(start, i + 1);
      }
    }
  }

  return null;
}

// 1) Get raw string from typical shapes
const item0 = $input.first()?.json ?? {};
let raw = '';

// Common patterns you might have
if (typeof item0.output !== 'undefined') raw = asString(item0.output);
else if (typeof item0.text !== 'undefined') raw = asString(item0.text);
else raw = asString(item0);

// 2) Clean up
const rawTrimmed = asString(raw).trim();
let clean = normaliseNewlines(stripCodeFences(rawTrimmed));

// Some models include multiple fences or backticks inside. Strip repeatedly if needed.
for (let i = 0; i < 3; i++) {
  const next = normaliseNewlines(stripCodeFences(clean));
  if (next === clean) break;
  clean = next;
}

// 3) Parse
let parsed = null;
let error = null;

let attempt = tryParseJson(clean);
if (attempt.ok) {
  parsed = attempt.value;
} else {
  // 4) Extract first JSON object substring and try again
  const extracted = extractFirstJsonObject(clean);
  if (extracted) {
    const attempt2 = tryParseJson(extracted);
    if (attempt2.ok) {
      parsed = attempt2.value;
    } else {
      error = 'Found JSON-like block but JSON.parse still failed';
    }
  } else {
    error = 'No JSON object block found';
  }
}

// 5) Return one item
return [
  {
    json: {
      parsed,
      raw: rawTrimmed,
      clean,
      error,
      // optional convenience fields, only if parsed is an object
      matter_heading: parsed?.matter?.heading ?? null,
      client_name: parsed?.matter?.D1?.client_name ?? null,
      recommendation: parsed?.recommendation ?? null,
      conversation_id: parsed?.submission_meta?.conversation_id ?? null,
    },
  },
];

// Get all items from the earlier Webhook node.
// Change "Webhook1" to the exact name of your webhook node, matching your workflow
const sourceItems = $items("Webhook") ?? [];
if (sourceItems.length === 0) {
  return [];
}

// Helper to walk any object/array and normalise all "email" fields
function normaliseEmails(obj) {
  if (obj == null || typeof obj !== "object") {
    return;
  }

  if (Array.isArray(obj)) {
    for (const item of obj) {
      normaliseEmails(item);
    }
    return;
  }

  for (const key of Object.keys(obj)) {
    const value = obj[key];

    // If property name is "email" and is a string. trim and lowercase it
    if (key === "email" && typeof value === "string") {
      obj[key] = value.trim().toLowerCase();
    }

    // Recurse into nested objects and arrays
    if (value && typeof value === "object") {
      normaliseEmails(value);
    }
  }
}

return sourceItems.map(item => {
  const out = {
    ...item,
    json: { ...item.json },
  };

  normaliseEmails(out.json);

  return out;
});

