 *  - parsed: the extracted JSON value (object/array) or null if it cannot be parsed
 *  - raw: original raw string (trimmed)
 *  - clean: cleaned string after stripping fences (trimmed)
 *  - error: parse error message (if any)
 */

function asString(v) {
  if (v === null || v === undefined) return '';
  if (typeof v === 'string') return v;
  try { return JSON.stringify(v); } catch { return String(v); }
}

function stripCodeFences(s) {
  // Remove leading/trailing triple backtick fences, optional language tag
  return s
    .replace(/^\s*```[a-zA-Z0-9_-]*\s*\n?/m, '')
    .replace(/\n?\s*```\s*$/m, '');
}

function normaliseNewlines(s) {
  return s
    .replace(/\/n\s*/g, '\n') // handle literal "/n" from some models
    .replace(/\r\n/g, '\n')
    .replace(/\r/g, '\n')
    .trim();
}

function tryParseJson(s) {
  // Attempt direct parse
  try { return { ok: true, value: JSON.parse(s) }; } catch (_) {}

  // If it is a JSON string that contains JSON, parse twice
  try {
    const once = JSON.parse(s);
    if (typeof once === 'string') {
      try { return { ok: true, value: JSON.parse(once) }; } catch (_) {}
    }
  } catch (_) {}

  return { ok: false, value: null };
}

function extractFirstJsonValue(s) {
  // Extract the first balanced {...} OR [...] JSON value from a string.
  // Ignores braces/brackets inside strings.
  let start = -1;
  let depth = 0;
  let opener = null; // '{' or '['
  let closer = null; // '}' or ']'
  let inString = false;
  let escape = false;

  const closerFor = (ch) => (ch === '{' ? '}' : ch === '[' ? ']' : null);

  for (let i = 0; i < s.length; i++) {
    const ch = s[i];

    if (escape) { escape = false; continue; }
    if (ch === '\\') { if (inString) escape = true; continue; }
    if (ch === '"') { inString = !inString; continue; }
    if (inString) continue;

    if (opener === null) {
      if (ch === '{' || ch === '[') {
        opener = ch;
        closer = closerFor(ch);
        start = i;
        depth = 1;
      }
      continue;
    }

    if (ch === opener) depth++;
    else if (ch === closer) {
      depth--;
      if (depth === 0 && start !== -1) {
        return s.slice(start, i + 1);
      }
    }
  }

  return null;
}

// 1) Get raw string from typical shapes
const item0 = $input.first()?.json ?? {};
let raw = '';

if (typeof item0.output !== 'undefined') raw = asString(item0.output);
else if (typeof item0.text !== 'undefined') raw = asString(item0.text);
else raw = asString(item0);

// 2) Clean up
const rawTrimmed = asString(raw).trim();
let clean = normaliseNewlines(stripCodeFences(rawTrimmed));

// Some models include multiple fences. Strip repeatedly if needed.
for (let i = 0; i < 3; i++) {
  const next = normaliseNewlines(stripCodeFences(clean));
  if (next === clean) break;
  clean = next;
}

// 3) Parse
let parsed = null;
let error = null;

let attempt = tryParseJson(clean);
if (attempt.ok) {
  parsed = attempt.value;
} else {
  // 4) Extract first JSON object/array substring and try again
  const extracted = extractFirstJsonValue(clean);
  if (extracted) {
    const attempt2 = tryParseJson(extracted);
    if (attempt2.ok) {
      parsed = attempt2.value;
    } else {
      error = 'Found JSON-like block but JSON.parse still failed';
    }
  } else {
    error = 'No JSON value block found';
  }
}

// 5) Convenience fields (only safe when parsed is an object, not an array)
const parsedObj = (parsed && typeof parsed === 'object' && !Array.isArray(parsed)) ? parsed : null;

return [
  {
    json: {
      parsed,
      raw: rawTrimmed,
      clean,
      error,
      matter_heading: parsedObj?.matter?.heading ?? null,
      client_name: parsedObj?.matter?.D1?.client_name ?? null,
      recommendation: parsedObj?.recommendation ?? null,
      conversation_id: parsedObj?.submission_meta?.conversation_id ?? null,
    },
  },
];

