parameters.jsCode:
// Flatten meeting summary JSON into a single machine-readable text stream

const data = $json.data?.summary ?? {};
let out = [];

function section(title, content) {
  if (!content) return;
  out.push(`## ${title}\n${content}\n`);
}

// Headline / type
if (data.meeting_type) {
  out.push(`# Meeting Type: ${data.meeting_type}\n`);
} else {
  out.push(`# Meeting Summary\n`);
}

// Keywords
if (Array.isArray(data.keywords) && data.keywords.length) {
  out.push(`## Keywords\n${data.keywords.join(', ')}\n`);
}

// Sections
section("Short Summary", data.short_summary);
section("Overview", data.overview);
section("Gist", data.gist);
section("Bullet Gist", data.bullet_gist);
section("Shorthand Bullet Notes", data.shorthand_bullet);
section("Action Items", data.action_items);

// Any transcript chapters or extended sections (if they exist)
if (Array.isArray(data.transcript_chapters) && data.transcript_chapters.length) {
  out.push(`## Transcript Chapters\n${JSON.stringify(data.transcript_chapters, null, 2)}\n`);
}
if (Array.isArray(data.extended_sections) && data.extended_sections.length) {
  out.push(`## Extended Sections\n${JSON.stringify(data.extended_sections, null, 2)}\n`);
}

// Final flattened text
return [{
  json: {
    text: out.join('\n')
  }
}];
