// Prepare Outlook search parameters
// Poll edu@legalengine.co.uk for emails with Adaptive/Onboarding in subject
// In production: change to adaptive-onboarding@simmons-simmons.com

// Load last poll timestamp from static data to avoid reprocessing
const lastPoll = $getWorkflowStaticData('global').last_poll_timestamp || null;
const now = new Date().toISOString();

let filterQuery = "(contains(subject, 'Adaptive') or contains(subject, 'Onboarding') or contains(subject, 'adaptive') or contains(subject, 'onboarding'))";

if (lastPoll) {
  // Only fetch emails received after last poll
  filterQuery += ` and receivedDateTime gt ${lastPoll}`;
}

// Save current timestamp for next run
$getWorkflowStaticData('global').last_poll_timestamp = now;

return [{
  json: {
    filter_query: filterQuery,
    last_poll: lastPoll,
    current_poll_time: now,
    graph_api_url: `https://graph.microsoft.com/v1.0/me/messages?$filter=${encodeURIComponent(filterQuery)}&$top=25&$orderby=receivedDateTime desc&$select=id,subject,body,from,receivedDateTime,conversationId,isRead`,
  }
}];

// Normalise Outlook message format and filter out already-processed emails
const response = $input.first().json;
const messages = response.value || [];

if (messages.length === 0) {
  console.log('No new messages found');
  return [];
}

console.log(`Found ${messages.length} messages matching filter`);

const normalisedMessages = messages.map(msg => ({
  message_id: msg.id,
  subject: msg.subject || '(no subject)',
  body: msg.body?.content || '',
  body_preview: (msg.body?.content || '').substring(0, 500),
  from_email: msg.from?.emailAddress?.address?.toLowerCase() || '',
  from_name: msg.from?.emailAddress?.name || '',
  received_at: msg.receivedDateTime,
  thread_id: msg.conversationId || msg.id,
  is_read: msg.isRead || false,
}));

// Filter out emails from the agent itself to avoid loops
const filteredMessages = normalisedMessages.filter(msg => {
  const fromEmail = msg.from_email;
  // Skip emails sent from the monitored inbox itself
  const excludeAddresses = ['edu@legalengine.co.uk', 'adaptive-onboarding@simmons-simmons.com'];
  return !excludeAddresses.includes(fromEmail);
});

console.log(`Processing ${filteredMessages.length} messages after filtering`);

// Return each message as a separate item for processing
return filteredMessages.map(msg => ({ json: msg }));

// Log polling run summary
const pollTime = $('Prepare Poll Request').first().json.current_poll_time;
console.log(`Poll completed at ${pollTime}. No new messages or processing complete.`);
return [{ json: { status: 'no_messages', poll_time: pollTime } }];
