// Parse conversation_history into structured array
// Handles Airtable's JSON wrapper format: {"state":"generated","value":"...","isStale":false}
// The value contains markdown-formatted matter entries separated by ", **NEW MATTER**,"

function extractMarkdownField(text, fieldName) {
  // Match markdown list format: "- Field: value" or "- Field: value (additional)"
  const regex = new RegExp(`- ${fieldName}:\\s*(.+?)(?:\\n|$)`, "i");
  const match = text.match(regex);
  return match ? match[1].trim() : null;
}

function extractMatterTitle(text) {
  // Match markdown header: "### Matter Title" or "### Confidential Matter X"
  const regex = /###\s+(.+?)(?:\n|$)/;
  const match = text.match(regex);
  return match ? match[1].trim() : null;
}

// Get raw data - might be string or object
let rawHistory = $json["Conversation history"] || "";
let historyContent = "";

// Handle JSON wrapper format
if (typeof rawHistory === "string") {
  try {
    const parsed = JSON.parse(rawHistory);
    if (parsed && parsed.value) {
      historyContent = parsed.value;
    } else {
      historyContent = rawHistory;
    }
  } catch (e) {
    // Not JSON, use as-is
    historyContent = rawHistory;
  }
} else if (typeof rawHistory === "object" && rawHistory.value) {
  historyContent = rawHistory.value;
}

const matters = [];

// Split by the **NEW MATTER** delimiter (with optional commas/whitespace)
const parts = historyContent.split(/,?\s*\*\*NEW MATTER\*\*,?\s*/);

for (const part of parts) {
  if (!part.trim()) continue;

  const matter = {
    matter_name: extractMatterTitle(part),
    matter_value: extractMarkdownField(part, "Matter value"),
    client: extractMarkdownField(part, "Client"),
    status: extractMarkdownField(part, "Status") || extractMarkdownField(part, "Completion"),
    lead_lawyer: extractMarkdownField(part, "Lead lawyer(?:s)?"),
    team: extractMarkdownField(part, "Team"),
    other_firms: extractMarkdownField(part, "Other firms/advisers") || extractMarkdownField(part, "Other firms and roles"),
    sector: extractMarkdownField(part, "Sector(?:s)?"),
    project: extractMarkdownField(part, "Project(?:s)?"),
    jurisdiction: extractMarkdownField(part, "Jurisdiction"),
    cross_border: extractMarkdownField(part, "Cross-border")
  };

  // Only include if we found at least a matter name
  if (matter.matter_name) {
    matters.push(matter);
  }
}

// Handle Clients field - also might have JSON wrapper
let clientsRaw = $json.Clients || "";
let clientsList = "";
if (typeof clientsRaw === "string") {
  try {
    const parsed = JSON.parse(clientsRaw);
    if (parsed && parsed.value) {
      clientsList = parsed.value;
    } else {
      clientsList = clientsRaw;
    }
  } catch (e) {
    clientsList = clientsRaw;
  }
} else if (typeof clientsRaw === "object" && clientsRaw.value) {
  clientsList = clientsRaw.value;
}

// Return structured data
return [{
  json: {
    matters: matters,
    matter_count: matters.length,
    clients: clientsList,
    raw_history: historyContent
  }
}];

// Helper to walk any object/array and lowercase all "email" fields
function lowercaseEmails(obj) {
  if (obj == null || typeof obj !== 'object') {
    return;
  }

  if (Array.isArray(obj)) {
    for (const el of obj) {
      lowercaseEmails(el);
    }
    return;
  }

  for (const key of Object.keys(obj)) {
    const value = obj[key];

    // If the property name is "email" and it is a string. lowercase it
    if (key === 'email' && typeof value === 'string') {
      obj[key] = value.toLowerCase();
    }

    // Recurse into nested objects or arrays
    if (value && typeof value === 'object') {
      lowercaseEmails(value);
    }
  }
}

return items.map(item => {
  // Clone json so we do not accidentally break references
  const out = {
    ...item,
    json: { ...item.json },
  };

  // This will hit body.email in your sample payload. plus any other email fields anywhere
  lowercaseEmails(out.json);

  return out;
});
