// Get all items from the earlier Webhook node.
// Change "Webhook" to the exact name of your webhook node.
const sourceItems = $items("Webhook");

// Helper to walk any object/array and normalise all "email" fields
function normaliseEmails(obj) {
  if (obj == null || typeof obj !== "object") {
    return;
  }

  if (Array.isArray(obj)) {
    for (const item of obj) {
      normaliseEmails(item);
    }
    return;
  }

  for (const key of Object.keys(obj)) {
    const value = obj[key];

    // If property name is "email" and is a string. trim and lowercase it
    if (key === "email" && typeof value === "string") {
      obj[key] = value.trim().toLowerCase();
    }

    // Recurse into nested objects and arrays
    if (value && typeof value === "object") {
      normaliseEmails(value);
    }
  }
}

return sourceItems.map(item => {
  const out = {
    ...item,
    json: { ...item.json },
  };

  normaliseEmails(out.json);

  return out;
});

const crypto = require('crypto');

// ============================================
// Configuration
// ============================================

const SHARED_SECRET = "wsec_1e982a89beaaac31fc7ea0145b8f970a86413a07b4e5f6b0d2acbfb11eab9d62";
const TIMESTAMP_TOLERANCE = 1800;

// ============================================
// Validation Function (never throws)
// ============================================

function validateHMAC() {
  try {
    // Extract signature header
    const signatureHeader = $json.headers['elevenlabs-signature'];

    if (!signatureHeader) {
      return {
        valid: false,
        statusCode: 401,
        error: 'missing_signature',
        message: 'Missing ElevenLabs-Signature header'
      };
    }

    // Get raw body from binary data
    if (!items || !items[0] || !items[0].binary || !items[0].binary.data || !items[0].binary.data.data) {
      return {
        valid: false,
        statusCode: 500,
        error: 'missing_raw_body',
        message: 'Missing raw body data'
      };
    }

    const base64Data = items[0].binary.data.data;
    const rawBody = Buffer.from(base64Data, 'base64').toString('utf8');

    // Parse signature header
    const parts = signatureHeader.split(',');
    const timestampPart = parts.find(p => p.startsWith('t='));
    const signaturePart = parts.find(p => p.startsWith('v0='));

    if (!timestampPart || !signaturePart) {
      return {
        valid: false,
        statusCode: 401,
        error: 'invalid_signature_format',
        message: 'Invalid signature header format'
      };
    }

    const timestamp = timestampPart.substring(2);
    const receivedSignature = signaturePart.substring(3);

    // Validate timestamp
    const currentTimestamp = Math.floor(Date.now() / 1000);
    const webhookTimestamp = parseInt(timestamp, 10);

    if (isNaN(webhookTimestamp)) {
      return {
        valid: false,
        statusCode: 401,
        error: 'invalid_timestamp',
        message: 'Invalid timestamp in signature header'
      };
    }

    const age = currentTimestamp - webhookTimestamp;

    if (age > TIMESTAMP_TOLERANCE) {
      return {
        valid: false,
        statusCode: 401,
        error: 'timestamp_too_old',
        message: `Webhook too old: ${age} seconds (tolerance: ${TIMESTAMP_TOLERANCE}s)`
      };
    }

    if (age < -60) {
      return {
        valid: false,
        statusCode: 401,
        error: 'timestamp_future',
        message: 'Webhook timestamp is in the future'
      };
    }

    // Compute expected signature
    const messageToSign = `${timestamp}.${rawBody}`;
    const expectedSignature = crypto
      .createHmac('sha256', SHARED_SECRET)
      .update(messageToSign, 'utf8')
      .digest('hex');

    // Compare signatures (timing-safe)
    const expectedBuffer = Buffer.from(expectedSignature, 'hex');
    const receivedBuffer = Buffer.from(receivedSignature, 'hex');

    if (expectedBuffer.length !== receivedBuffer.length) {
      return {
        valid: false,
        statusCode: 401,
        error: 'signature_length_mismatch',
        message: 'Signature length mismatch'
      };
    }

    if (!crypto.timingSafeEqual(expectedBuffer, receivedBuffer)) {
      return {
        valid: false,
        statusCode: 401,
        error: 'invalid_signature',
        message: 'Invalid signature - request not authenticated'
      };
    }

    // Success!
    return {
      valid: true,
      statusCode: 200,
      timestamp: webhookTimestamp,
      age: age,
      raw_body_length: rawBody.length
    };

  } catch (error) {
    return {
      valid: false,
      statusCode: 500,
      error: 'validation_error',
      message: `Validation error: ${error.message}`
    };
  }
}

// ============================================
// Execute Validation and Return Result
// ============================================

const result = validateHMAC();

// Log result
console.log(JSON.stringify({
  event: result.valid ? 'webhook_authenticated' : 'webhook_rejected',
  timestamp: new Date().toISOString(),
  ...result
}));

// ALWAYS return success (never throw)
// Use an IF node after this to route based on result.valid
return {
  json: {
    // Validation result
    hmac_valid: result.valid,
    hmac_status_code: result.statusCode,
    hmac_error: result.error || null,
    hmac_message: result.message || 'Signature valid',

    // Original webhook data (for success path)
    ...(result.valid ? $json.body : {}),

    // Metadata (for both paths)
    timestamp: result.timestamp || null,
    age: result.age || null
  }
};

