parameters.jsCode:
/**
 * Robust JSON -> Markdown transformer for Airtable storage (turn-by-turn).
 * - Produces a numbered transcript table AND an expanded section so it's crystal clear who said what.
 * - Safe against missing/variant fields; always returns something useful.
 * Output fields: Markdown, Title, Summary, Type, ConversationId, EventISO, Status
 */

function get(obj, path, fallback = undefined) {
  try { return path.split('.').reduce((o, k) => (o && k in o ? o[k] : undefined), obj) ?? fallback; }
  catch { return fallback; }
}

function toISO(ts) {
  if (!ts && ts !== 0) return undefined;
  const n = Number(ts);
  const ms = n > 1e12 ? n : n * 1000;
  const d = new Date(ms);
  return isNaN(d.getTime()) ? undefined : d.toISOString();
}

function cleanStr(x) {
  if (x == null) return '';
  if (typeof x === 'string') return x.replace(/\s+$/g, '').replace(/\u0000/g, '');
  try { return JSON.stringify(x, null, 2); } catch { return String(x); }
}

function uniq(arr) { return Array.from(new Set(arr.filter(Boolean))); }

function trunc(str, max = 15000) {
  if (!str || str.length <= max) return str || '';
  const head = str.slice(0, max - 1000);
  const tail = str.slice(-800);
  return head + `\n\n---\n*…truncated (${str.length - max + 1000} chars omitted)…*\n---\n` + tail;
}

const item = items?.[0]?.json ?? {};
const body = get(item, 'body', item);
const data = get(body, 'data', body);
const headers = get(item, 'headers', get(body, 'headers', {}));
const query = get(item, 'query', get(body, 'query', {}));
const params = get(item, 'params', get(body, 'params', {}));

const type = get(body, 'type', get(item, 'type', 'unknown'));
const eventTs = get(body, 'event_timestamp', get(item, 'event_timestamp'));
const eventISO = toISO(eventTs);
const conversationId = get(data, 'conversation_id') || get(item, 'conversation_id') || 'unknown';
const agentId = get(data, 'agent_id') || get(item, 'agent_id');
const status = get(data, 'status') || get(item, 'status');

const transcript = Array.isArray(get(data, 'transcript'))
  ? get(data, 'transcript')
  : Array.isArray(get(item, 'transcript')) ? get(item, 'transcript') : [];

// Normalize each turn and build artifacts
let userCount = 0, agentCount = 0, lastTurnSeconds = 0;
const norm = (t = {}) => {
  const roleRaw = t.role || t.speaker || t.name || 'unknown';
  const role = roleRaw.toLowerCase() === 'assistant' ? 'agent'
             : roleRaw.toLowerCase() === 'system' ? 'system'
             : roleRaw.toLowerCase();
  const speaker = role === 'agent' ? 'Agent'
                : role === 'user' ? 'User'
                : roleRaw[0]?.toUpperCase() + roleRaw.slice(1);
  const msg = cleanStr(t.message ?? t.text ?? t.content ?? '');
  const nodeId = get(t, 'agent_metadata.workflow_node_id');
  const interrupted = !!t.interrupted;
  const secs = typeof t.time_in_call_secs === 'number' ? t.time_in_call_secs : undefined;
  if (role === 'user') userCount++;
  if (role === 'agent') agentCount++;
  if (secs != null) lastTurnSeconds = Math.max(lastTurnSeconds, secs);
  return { speaker, role, msg, nodeId, interrupted, secs };
};

const turns = (transcript || []).map(norm);

// Quick heuristics for jurisdictions & amounts
const fullText = transcript.map(t => `${t?.role || ''}: ${t?.message || ''}`).join('\n').toLowerCase();
const countryHints = ['uk','united kingdom','england','wales','scotland','northern ireland','united states','us','usa','netherlands','ireland','france','germany','spain','italy','uae','dubai'];
const detectedJurisdictions = uniq(
  countryHints.filter(k => fullText.includes(k)).map(k => {
    if (k === 'us' || k === 'usa' || k === 'united states') return 'United States';
    if (k === 'uk' || k === 'united kingdom') return 'United Kingdom';
    return k[0].toUpperCase() + k.slice(1);
  })
);
const moneyRegex = /(?:£|\$|€)\s?\d{1,3}(?:[,\d]{0,12})(?:\.\d+)?(?:\s?(?:k|m|b|million|billion))?/gi;
const detectedAmounts = uniq((fullText.match(moneyRegex) || []).map(s => s.trim()));

// Possible names
const nameRegex = /\b([A-Z][a-z]+(?:\s+[A-Z][a-z]+){0,2})\b/g;
const possibleNames = uniq(
  (transcript || [])
    .filter(t => t?.message)
    .flatMap(t => Array.from(t.message.matchAll(nameRegex)).map(m => m[1]))
).filter(n => !['I','It','Yes','No','Hi','UK','US'].includes(n));

// Metadata table
const metaRows = [
  ['Type', String(type || 'unknown')],
  ['Status', String(status || 'unknown')],
  ['Event (epoch)', eventTs != null ? String(eventTs) : ''],
  ['Event (ISO)', eventISO || ''],
  ['Conversation ID', conversationId],
  ['Agent ID', agentId || ''],
  ['Agent turns', String(agentCount)],
  ['User turns', String(userCount)],
  ['Last reported call secs', String(lastTurnSeconds || 0)],
];

function table(rows) {
  const header = `| Key | Value |\n|---|---|`;
  const body = rows.map(r => `| ${r[0] ?? ''} | ${(r[1] ?? '').toString().replace(/\n/g, ' ')} |`).join('\n');
  return `${header}\n${body}`;
}

// Turn-by-turn TABLE
function transcriptTable(ts) {
  const header = `| # | Speaker | Message |\n|---:|---|---|`;
  const body = ts.length
    ? ts.map((t, i) => {
        const msg = (t.msg || '*no message*').replace(/\n/g, ' ').slice(0, 2000);
        return `| ${i+1} | ${t.speaker} | ${msg} |`;
      }).join('\n')
    : `| 1 | - | _No transcript entries found_ |`;
  return `${header}\n${body}`;
}

// Turn-by-turn EXPANDED blocks
function transcriptExpanded(ts) {
  if (!ts.length) return '_No transcript entries found_';
  return ts.map((t, i) => {
    const meta = [];
    if (typeof t.secs === 'number') meta.push(`t=${t.secs.toFixed(1)}s`);
    if (t.nodeId) meta.push(`node:${t.nodeId}`);
    if (t.interrupted) meta.push('interrupted');
    const metaLine = meta.length ? ` _(${meta.join(' · ')})_` : '';
    return `### Turn ${i+1} — **${t.speaker}**${metaLine}\n\n> ${t.msg || '*no message*'}`;
  }).join('\n\n');
}

const titleParts = [
  type ? String(type).replace(/_/g, ' ') : 'Conversation',
  conversationId !== 'unknown' ? `· ${conversationId}` : '',
].filter(Boolean);

const title = titleParts.join(' ');
const whenLine = eventISO ? `Recorded: ${eventISO}` : '';

const jurisdictionsMd = detectedJurisdictions.length
  ? detectedJurisdictions.map(j => `- ${j}`).join('\n')
  : '- Not clearly stated';

const amountsMd = detectedAmounts.length
  ? detectedAmounts.map(a => `- ${a}`).join('\n')
  : '- None detected';

const participantsMd = possibleNames.length
  ? uniq(possibleNames).slice(0, 20).map(n => `- ${n}`).join('\n')
  : '- Not extracted';

// Base sections
let md = `# ${title}

${whenLine}

## Metadata
${table(metaRows)}

## Jurisdictions (detected)
${jurisdictionsMd}

## Amounts (detected)
${amountsMd}

## Participants (possible)
${participantsMd}

## Transcript (turn-by-turn table)
${transcriptTable(turns)}

## Transcript (expanded)
${transcriptExpanded(turns)}
`;

// Raw payload snapshots (collapsed)
const rawHeader = cleanStr(headers);
const rawQuery = cleanStr(query);
const rawParams = cleanStr(params);
const rawBody = cleanStr(body);

md += `

---

## Raw (truncated)

<details>
<summary>Headers</summary>

\`\`\`json
${trunc(rawHeader, 4000)}
\`\`\`
</details>

<details>
<summary>Query</summary>

\`\`\`json
${trunc(rawQuery, 2000)}
\`\`\`
</details>

<details>
<summary>Params</summary>

\`\`\`json
${trunc(rawParams, 2000)}
\`\`\`
</details>

<details>
<summary>Body</summary>

\`\`\`json
${trunc(rawBody, 12000)}
\`\`\`
</details>
`;

const summary = `Agent ${agentId || 'unknown'} vs User · ${userCount} user turns / ${agentCount} agent turns${detectedAmounts.length ? ' · ' + detectedAmounts.slice(0,2).join(', ') : ''}`;

return [{
  json: {
    Markdown: md,
    Title: title || 'Conversation',
    Summary: summary,
    Type: type || 'unknown',
    ConversationId: conversationId,
    EventISO: eventISO || '',
    Status: status || '',
  }
}];

