      leadLawyer: D5?.lead_lawyer?.name ?? null,
      startDate: D8?.start_date ?? null,
      completed: Boolean(D8?.completed),
      pressLinks: D9?.press_coverage_links ?? []
    };
  }
  // Otherwise, return whatever the fallback/JSON produced
  return parsed;
})();

return [{ json: out }];

/**
 * n8n Code node
 * Input: first item has { output: string | object }
 * Handles:
 *  - raw JSON object
 *  - JSON string
 *  - Markdown fenced JSON ```json ... ```
 *  - Strings that contain JSON plus extra text
 *  - Fallback to sectioned headings parser
 */

const raw = $input.first()?.json?.output;

const normaliseString = (s) =>
  String(s ?? '')
    .replace(/\/n\s*/g, '\n') // handle literal /n from models
    .replace(/\r\n/g, '\n')
    .replace(/\r/g, '\n')
    .trim();

function stripFences(s) {
  // Pull the content out of ```json ... ``` or ``` ... ```
  const m = s.match(/```(?:json)?\s*([\s\S]*?)\s*```/i);
  return m ? m[1].trim() : s.trim();
}

function tryParseJson(s) {
  // 1) direct parse
  try {
    return JSON.parse(s);
  } catch (_) {}

  // 2) parse after stripping fences
  const unfenced = stripFences(s);
  if (unfenced !== s) {
    try {
      return JSON.parse(unfenced);
    } catch (_) {}
  }

  // 3) if the parsed result is a JSON string, parse again
  try {
    const once = JSON.parse(unfenced);
    if (typeof once === 'string') {
      try {
        return JSON.parse(once);
      } catch (_) {
        return once;
      }
    }
    return once;
  } catch (_) {}

  // 4) last resort: extract JSON payload by brackets
  const firstObj = unfenced.indexOf('{');
  const lastObj = unfenced.lastIndexOf('}');
  if (firstObj !== -1 && lastObj !== -1 && lastObj > firstObj) {
    const candidate = unfenced.slice(firstObj, lastObj + 1);
    try {
      return JSON.parse(candidate);
    } catch (_) {}
  }

  const firstArr = unfenced.indexOf('[');
  const lastArr = unfenced.lastIndexOf(']');
  if (firstArr !== -1 && lastArr !== -1 && lastArr > firstArr) {
    const candidate = unfenced.slice(firstArr, lastArr + 1);
    try {
      return JSON.parse(candidate);
    } catch (_) {}
  }

  return null;
}

function parseSectionedFallback(s) {
  const get = (label) => {
    const esc = label.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
    const m = s.match(new RegExp(`${esc}:\\s*\\n([\\s\\S]*?)(?:\\n\\n|$)`));
    return m ? m[1].trim() : '';
  };

  const formatsRaw = get('Potential Formats');
  const potentialFormats = formatsRaw
    .split('\n')
    .map((line) => line.replace(/^[-•]\s*/, '').trim())
    .filter(Boolean);

  return {
    areaToExplore: get('Area to Explore'),
    potentialFormats,
    formattedFormatsHTML: potentialFormats.map((f) => `<li>${f}</li>`).join(''),
    suggestedUse: get('Suggested Use'),
    somethingToThinkAbout: get('Something to Think About'),
  };
}

function parseOutput(input) {
  // If n8n already gave us an object, keep it
  if (input && typeof input === 'object' && !Array.isArray(input)) return input;

  const s = normaliseString(input);

  // Try to parse JSON (covers fenced JSON too)
  const parsed = tryParseJson(s);
  if (parsed !== null && parsed !== undefined) return parsed;

  // Fallback to sectioned text parsing
  return parseSectionedFallback(s);
}

const parsed = parseOutput(raw);

// Normalise to usable variables. Flatten if it's the "matter" schema
const out = (() => {
  if (parsed && typeof parsed === 'object' && parsed.matter) {
    const { matter, submission_meta, recommendation } = parsed;
    const { is_confidential, D1, D2, D3, D4, D5, D6, D7, D8, D9 } = matter || {};

    const pressLinks = (D9?.press_coverage_links ?? [])
      .map((x) => String(x ?? '').trim())
      .filter(Boolean);

    return {
      matter,
      submission_meta,
      recommendation,
      is_confidential,

      D1,
      D2,
      D3,
      D4,
      D5,
      D6,
      D7,
      D8,
      D9,

      // Handy aliases
      client: D1?.client_name ?? null,
      matterDescription: D2?.matter_description ?? null,
      matterValue: D3?.matter_value?.amount ?? null,
      matterCurrency: D3?.matter_value?.currency ?? null,
      jurisdictions: D4?.jurisdictions ?? [],
      leadLawyer: D5?.lead_lawyer?.name ?? null,
      startDate: D8?.start_date ?? null,
      endDate: D8?.end_date ?? null,
      completed: Boolean(D8?.completed),
      pressLinks,
    };
  }

  return parsed;
})();

return [{ json: out }];

/**
 * n8n Code node (robust parse + clean normalisation)
 * Fixes: "Bad control character in string literal" by only converting \n outside strings.
 *
 * Output shape:
 * {
 *   matter,
 *   submission_meta,
 *   recommendation,
 *   fields: { is_confidential, D1..D9, client, matterDescription, ... },
 *   __parse_meta (only if useful)
 * }
 */

const item = $input.first()?.json ?? {};
const raw =
  item.output ??
  item.text ??
  item.response ??
  item.data ??
  item.result ??
  item;

/* ----------------------------- helpers ----------------------------- */

function isPlainObject(v) {
  return v !== null && typeof v === 'object' && !Array.isArray(v);
}

function asString(v) {
  if (v === null || v === undefined) return '';
  if (typeof v === 'string') return v;
  try { return JSON.stringify(v); } catch { return String(v); }
}

function normaliseSurface(v) {
  let s = asString(v).replace(/\uFEFF/g, '');
  // normalise actual line endings only
  s = s.replace(/\r\n/g, '\n').replace(/\r/g, '\n');
  // smart quotes
  s = s.replace(/[“”]/g, '"').replace(/[‘’]/g, "'");
  return s.trim();
}

function stripCodeFences(s) {
  const t = s.trim();
  const m =
    t.match(/```(?:json|js|javascript)?\s*([\s\S]*?)\s*```/i) ||
    t.match(/~~~(?:json|js|javascript)?\s*([\s\S]*?)\s*~~~/i);
  return m?.[1]?.trim() ?? t;
}

function unwrapParens(s) {
  let t = s.trim();
  for (let i = 0; i < 3; i++) {
    if (t.startsWith('(') && t.endsWith(')')) {
      const inner = t.slice(1, -1).trim();
      if (inner.startsWith('{') || inner.startsWith('[')) {
        t = inner;
        continue;
      }
    }
    break;
  }
  return t;
}

/**
 * Convert escape sequences like \n \t \r ONLY when they occur outside JSON strings.
 * Also, if there are actual control chars inside a quoted string, escape them.
 */
function sanitiseJsonText(text) {
  const s = normaliseSurface(text);

  let out = '';
  let inString = false;
  let quote = null;
  let escape = false;

  for (let i = 0; i < s.length; i++) {
    const ch = s[i];

    if (inString) {
      if (escape) {
        out += ch;
        escape = false;
        continue;
      }
      if (ch === '\\') {
        out += ch;
        escape = true;
        continue;
      }
      if (ch === quote) {
        out += ch;
        inString = false;
        quote = null;
        continue;
      }

      // Escape actual control chars inside strings if they exist
      if (ch === '\n') { out += '\\n'; continue; }
      if (ch === '\t') { out += '\\t'; continue; }
      // (CR should not appear after normaliseSurface, but safe)
      if (ch === '\r') { out += '\\n'; continue; }

      out += ch;
      continue;
    }

    // not in string
    if (ch === '"' || ch === "'") {
      inString = true;
      quote = ch;
      out += ch;
      continue;
    }

    // Convert "\r\n" (as four chars: backslash r backslash n) into a single newline
    if (ch === '\\' && s[i + 1] === 'r' && s[i + 2] === '\\' && s[i + 3] === 'n') {
      out += '\n';
      i += 3;
      continue;
    }

    // Convert escapes outside strings into whitespace/control
    if (ch === '\\') {
      const n = s[i + 1];
      if (n === 'n') { out += '\n'; i++; continue; }
      if (n === 'r') { out += '\n'; i++; continue; }
      if (n === 't') { out += '\t'; i++; continue; }
      // leave other escapes alone
      out += ch;
      continue;
    }

    // Some models use "/n" outside strings
    if (ch === '/' && s[i + 1] === 'n') {
      out += '\n';
      i++;
      continue;
    }

    out += ch;
  }

  return out.trim();
}

// Balanced extraction, ignores braces inside quoted strings
function extractFirstBalancedJson(s) {
  const text = s;
  const iObj = text.indexOf('{');
  const iArr = text.indexOf('[');
  const start =
    iObj === -1 ? iArr :
    iArr === -1 ? iObj :
    Math.min(iObj, iArr);

  if (start === -1) return null;

  const stack = [];
  let inString = false;
  let quote = null;
  let escape = false;

  for (let i = start; i < text.length; i++) {
    const ch = text[i];

    if (inString) {
      if (escape) { escape = false; continue; }
      if (ch === '\\') { escape = true; continue; }
      if (ch === quote) { inString = false; quote = null; }
      continue;
    }

    if (ch === '"' || ch === "'") { inString = true; quote = ch; continue; }

    if (ch === '{' || ch === '[') { stack.push(ch); continue; }

    if (ch === '}' || ch === ']') {
      if (!stack.length) return null;
      const open = stack.pop();
      const ok = (open === '{' && ch === '}') || (open === '[' && ch === ']');
      if (!ok) return null;
      if (!stack.length) return text.slice(start, i + 1).trim();
    }
  }

  return null;
}

function removeTrailingCommas(s) {
  let out = '';
  let inString = false;
  let quote = null;
  let escape = false;

  for (let i = 0; i < s.length; i++) {
    const ch = s[i];

    if (inString) {
      out += ch;
      if (escape) { escape = false; continue; }
      if (ch === '\\') { escape = true; continue; }
      if (ch === quote) { inString = false; quote = null; }
      continue;
    }

    if (ch === '"' || ch === "'") { inString = true; quote = ch; out += ch; continue; }

    if (ch === ',') {
      let j = i + 1;
      while (j < s.length && /\s/.test(s[j])) j++;
      if (s[j] === '}' || s[j] === ']') continue;
    }

    out += ch;
  }

  return out;
}

function tryParseJson(any) {
  const errors = [];

  if (isPlainObject(any)) return { value: any, meta: { method: 'already_object', errors } };
  if (Array.isArray(any) && any.length === 1 && isPlainObject(any[0])) {
    return { value: any[0], meta: { method: 'single_object_array', errors } };
  }

  const base0 = normaliseSurface(any);
  const unfenced0 = stripCodeFences(base0);
  const unwrapped0 = unwrapParens(unfenced0);

  const base = sanitiseJsonText(unwrapped0);

  const candidates = [];
  const push = (label, val) => {
    const v = (val ?? '').trim();
    if (v) candidates.push({ label, v });
  };

  push('sanitised', base);

  const extracted = extractFirstBalancedJson(base);
  if (extracted && extracted !== base) push('extracted', extracted);

  for (const c of candidates) {
    try {
      return { value: JSON.parse(c.v), meta: { method: c.label, errors } };
    } catch (e) {
      errors.push(`${c.label}: ${e?.message ?? String(e)}`);
    }

    const repaired = removeTrailingCommas(c.v);
    if (repaired !== c.v) {
      try {
        return { value: JSON.parse(repaired), meta: { method: `${c.label}+repair`, repaired: 'trailing_commas', errors } };
      } catch (e) {
        errors.push(`${c.label}+repair: ${e?.message ?? String(e)}`);
      }
    }
  }

  return { value: null, meta: { method: 'failed', errors, sample: base.slice(0, 500) } };
}

/* ----------------------------- normalisation ----------------------------- */

function nonEmptyString(v) {
  const s = String(v ?? '').trim();
  return s ? s : null;
}

function coerceBoolOrNull(v) {
  if (v === true || v === false) return v;
  if (v === 'true') return true;
  if (v === 'false') return false;
  return null;
}

function coerceNumberOrNull(v) {
  if (v === null || v === undefined || v === '') return null;
  const n = Number(v);
  return Number.isFinite(n) ? n : null;
}

function cleanStringArray(v) {
  if (!Array.isArray(v)) return [];
  return v.map((x) => String(x ?? '').trim()).filter(Boolean);
}

function flattenMatter(obj) {
  const matter = isPlainObject(obj?.matter) ? obj.matter : null;
  if (!matter) return null;

  const D1 = matter.D1 ?? null;
  const D2 = matter.D2 ?? null;
  const D3 = matter.D3 ?? null;
  const D4 = matter.D4 ?? null;
  const D5 = matter.D5 ?? null;
  const D6 = matter.D6 ?? null;
  const D7 = matter.D7 ?? null;
  const D8 = matter.D8 ?? null;
  const D9 = matter.D9 ?? null;

  return {
    is_confidential: coerceBoolOrNull(matter.is_confidential),

    D1, D2, D3, D4, D5, D6, D7, D8, D9,

    client: nonEmptyString(D1?.client_name),
    matterDescription: nonEmptyString(D2?.matter_description),
    matterValue: coerceNumberOrNull(D3?.matter_value?.amount),
    matterCurrency: nonEmptyString(D3?.matter_value?.currency),

    jurisdictions: cleanStringArray(D4?.jurisdictions),
    leadLawyer: nonEmptyString(D5?.lead_lawyer?.name),

    startDate: nonEmptyString(D8?.start_date),
    endDate: nonEmptyString(D8?.end_date),

    completed: coerceBoolOrNull(D8?.completed),

    pressLinks: cleanStringArray(D9?.press_coverage_links),
  };
}

/* ----------------------------- main ----------------------------- */

const parsed = tryParseJson(raw);
let obj = parsed.value;

if (Array.isArray(obj)) {
  const pick = obj.find((x) => isPlainObject(x) && (x.matter || x.submission_meta || x.recommendation));
  obj = pick ?? obj[0] ?? null;
}

// Allow common wrappers
if (obj && !obj.matter && isPlainObject(obj.data) && obj.data.matter) obj = obj.data;
if (obj && !obj.matter && isPlainObject(obj.result) && obj.result.matter) obj = obj.result;

if (!obj) {
  return [{
    json: {
      error: 'Unable to parse input as JSON.',
      __parse_meta: {
        method: parsed.meta.method,
        errors: (parsed.meta.errors || []).slice(0, 10),
        sample: parsed.meta.sample ?? null,
      },
    }
  }];
}

const fields = flattenMatter(obj) ?? {};

const out = {
  matter: isPlainObject(obj.matter) ? obj.matter : null,
  submission_meta: isPlainObject(obj.submission_meta) ? obj.submission_meta : (obj.submission_meta ?? null),
  recommendation: obj.recommendation ?? null,
  fields,
};

const hasErrors = (parsed.meta.errors || []).length > 0;
const nonTrivial = parsed.meta.method !== 'already_object' && parsed.meta.method !== 'single_object_array';
if (hasErrors || nonTrivial) {
  out.__parse_meta = {
    method: parsed.meta.method,
    repaired: parsed.meta.repaired ?? null,
    errors: (parsed.meta.errors || []).slice(0, 10),
  };
}

return [{ json: out }];
